function fcInit(obj) {	
    dsOutCConfirmRecord.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if (!fnc_IsMandatory(cboStndYear, "년도")) return false;
   return true;
}

//조회 
function fcSearch(obj) {
	if (!fcSearchCheck()) return;
    
    var sServiceName = "d02000037-service:searchCConfirmRecord";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutCConfirmRecord=dsOutCConfirmRecord";
    
    fcd_AddParam("RACE_YY", cboStndYear.Value);
    fcd_AddParam("CAND_NM", edName.Text);
    fcd_AddParam("GRD", cboGrd.Value);
	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutCConfirmRecord))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	
	// 필수항목체크
	oDataSet          = dsOutCConfirmRecord;
	sMandatoryColID   = "RACE_YY|CAND_NM";
	sMandatoryColName = "년도|선수명";
	oGrid             = grdPageList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
	var sServiceName = "d02000037-service:saveCConfirmRecord";
    var sInDataSet   = "dsOutCConfirmRecord=dsOutCConfirmRecord:u";
    var sOutDataSet  = "dsOutCConfirmRecord=dsOutCConfirmRecord";
	
	fcd_AddParam("RACE_YY", cboStndYear.Value);
    fcd_AddParam("CAND_NM", edName.Text);
    fcd_AddParam("GRD", cboGrd.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

