>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
	
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var nPERIO_NO = dsOutCConfirmRecord.GetColumn(0, "RACER_PERIO_NO"); //ToNumber(dsOutCConfirmRecord.GetColumn(0, "RACER_PERIO_NO"));
	
	//var nPERIO_NO = 2027 - nYear;
	
	var sSubUrl   = "";                     						//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "d02009037.jrf";								//레포트 파일명
	var sDataSet  = "dsOutCConfirmRecord=dsOutCConfirmRecord";		//"UBI 쿼리명, MIP Dataset명"
	var sArg      = "IMG_URL="+logImgUrl;
		sArg 	 += " PERIO_NO="+nPERIO_NO;

	var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
	
	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	
}
