function fcSearchUseGbnItem(){

	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
    
    sServiceName  = "d02000040-service:searchUseGbnItem";
	sInDataSet    = "";
	sOutDataSet   = " dsUseGbnItem=dsUseGbnItem dsUseItem=dsUseItem dsSndExamRateBas=dsSndExamRateBas dsSeltChng=dsSeltChng ";
	
	fcd_AddParam("racerPerioNo", cbSearchPerioNo.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function fcUseEditable(){

	for (var i=0;i<dsUseItem.RowCount();i++){  
		var itemCode = dsUseItem.GetColumn(i,"ITEM_CD");
		switch(itemCode){
		   case "301" :
			tabMain.tab1.grdPerioExam1.SetCellProp("Body",3,"Edit","mask");
			break;
		   case "302" :
			tabMain.tab1.grdPerioExam1.SetCellProp("Body",5,"Edit","mask");
			break;
		   case "303" :
			tabMain.tab1.grdPerioExam1.SetCellProp("Body",7,"Edit","mask");
			break;
		   case "304" :
			tabMain.tab1.grdPerioExam1.SetCellProp("Body",9,"Edit","mask");
			break;
		   case "305" :
			tabMain.tab1.grdPerioExam1.SetCellProp("Body",11,"Edit","mask");
			break;	
		}
	}	
}

function fcSearchCheck() {
  if( fnc_IsNull(cbSearchPerioNo.Value) ) {
        // 기수 누락
        fnc_Message(SNIS_CAN_0008, "N", "기수");
        return false;
    }
	return true;
}

//조회버튼 클릭 시
function fcSearch(obj) {
 
   if ( !fcSearchCheck(obj) ) return;

	 fcSearchUseGbnItem();  //2008.05.26 update
}


