/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {
	if ( !fnc_DatasetChangeCheck(dsPerioExam1)
			&& !fnc_DatasetChangeCheck(dsPerioExam2)
			&& !fnc_DatasetChangeCheck(dsPerioExam4)){
			fnc_Message(SNIS_COM_1006, "N");	
			return false;
	}
		return true;
}


function fcSave(obj) {
    
   if ( !fcSaveCheck() ) return;

	var sServiceName  = "d02000040-service:savePerioExam1";
	var sInDataSet    = "dsPerioExam1=dsPerioExam1:u dsPerioExam4=dsPerioExam4:u dsPerioExam2=dsPerioExam2:u ";
	var sOutDataSet   = "dsPerioExam1=dsPerioExam1 dsPerioExam4=dsPerioExam4 dsPerioExam2=dsPerioExam2 ";

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


