/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
	switch(sServiceName) {
        // 자전거실기 조회 콜백
        case "d02000040-service:searchUseGbnItem"  :
				fcUseEditable();
				fcSearchPerioExam1();
        		break; 
        case "d02000040-service:searchPerioExam1" :
				fcPassYn();
				break; 
        case "d02000040-service:savePerioExam1" :
				fcSearchPerioExam1();
				break;                              
		default :
			break;
	}	
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

