}

//평균점수 구하는 함수
function fcAvgScr(obj,nRow){
	
	var scrCnt = 0;
	var totScr = 0;
	var avgScr = 0;
	
	if(obj.id == "dsPerioExam1"){
		if(length(dsPerioExam1.GetColumn(nRow,"REC_200")) > 0){
			scrCnt++;
			totScr += toNumber(dsPerioExam1.GetColumn(nRow,"SCR_200"));
		}
		if(length(dsPerioExam1.GetColumn(nRow,"REC_333")) > 0){
			scrCnt++;
			totScr += toNumber(dsPerioExam1.GetColumn(nRow,"SCR_333"));
		}
		if(length(dsPerioExam1.GetColumn(nRow,"REC_500")) > 0){
			scrCnt++;
			totScr += toNumber(dsPerioExam1.GetColumn(nRow,"SCR_500"));
		}
		if(length(dsPerioExam1.GetColumn(nRow,"REC_1000")) > 0){
			scrCnt++;
			totScr += toNumber(dsPerioExam1.GetColumn(nRow,"SCR_1000"));
		}				
		if(length(dsPerioExam1.GetColumn(nRow,"REC_2000")) > 0){
			scrCnt++;
			totScr += toNumber(dsPerioExam1.GetColumn(nRow,"SCR_2000"));
		}

		avgScr = Round(ToNumber(totScr / scrCnt),2);	
		dsPerioExam1.SetColumn(nRow,"TOT_SCR",totScr); 	
		dsPerioExam1.SetColumn(nRow,"AVG_SCR",avgScr); //평균점수 셋팅
		
	}else if(obj.id == "dsPerioExam2"){
		
		 var scrCnt = 0;
		 var totScr = 0;
		 var avgScr = 0;
		if(!fnc_isNull(dsPerioExam2.GetColumn(nRow,"LESSON1"))){
			scrCnt++;
			totScr += toNumber(dsPerioExam2.GetColumn(nRow,"LESSON1"));
		}
		if(!fnc_isNull(dsPerioExam2.GetColumn(nRow,"LESSON2"))){
			scrCnt++;
			totScr += toNumber(dsPerioExam2.GetColumn(nRow,"LESSON2"));
		}
		if(!fnc_isNull(dsPerioExam2.GetColumn(nRow,"LESSON3"))){
			scrCnt++;
			totScr += toNumber(dsPerioExam2.GetColumn(nRow,"LESSON3"));
		}
		if(!fnc_isNull(dsPerioExam2.GetColumn(nRow,"LESSON4"))){
			scrCnt++;
			totScr += toNumber(dsPerioExam2.GetColumn(nRow,"LESSON4"));
		}
		avgScr = Round(ToNumber(totScr / scrCnt),2);	
		dsPerioExam2.SetColumn(nRow,"LESSON_TOT_SCR",totScr); 	
		dsPerioExam2.SetColumn(nRow,"LESSON_AVG_SCR",avgScr); //평균점수 셋팅
	}
}
function dsPerioExam1_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var iRec = ToFloat(varNewValue);
	var iScr = 0;
	
	switch(strColumnID){
		case "REC_200"  : dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='301'"); break;
		case "REC_333"  : dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='302'"); break;
		case "REC_500"  : dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='303'"); break;
		case "REC_1000" : dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='304'"); break;
		case "REC_2000" : dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='305'"); break;
	default : break;
	}
	
	for(i=0;i<dsSeltChng.RowCount();i++){
			var iStartRec = toNumber(dsSeltChng.GetColumn(i, "START_REC")); 
			var iEndRec = toNumber(dsSeltChng.GetColumn(i, "END_REC"));
			if( (iRec >= iStartRec)&&(iRec <=iEndRec)){
				 iScr = dsSeltChng.getColumn(i,"BAS_SCR");
			 }
		}
	
	switch(strColumnID){
		case "REC_200"  : dsPerioExam1.SetColumn(nRow,"SCR_200",iScr);   break;
		case "REC_333"  : dsPerioExam1.SetColumn(nRow,"SCR_333",iScr);   break;
		case "REC_500"  : dsPerioExam1.SetColumn(nRow,"SCR_500",iScr);   break;
		case "REC_1000" : dsPerioExam1.SetColumn(nRow,"SCR_1000",iScr);  break;
		case "REC_2000" : dsPerioExam1.SetColumn(nRow,"SCR_2000",iScr);  break;
	default : break;
	}
	fcAvgScr(obj, nRow);// 평균,환산점수
}
function dsPerioExam4_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var tot_time = 0;
	var iRec = 0;
	var iScr = 0;
	switch(strColumnID){
		case "TIME_REC" : 
		case "ERROR_REC" : 
			dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='306'");
			
		    var m = Substr(dsPerioExam4.GetColumn(nRow,"TIME_REC"),0,2);
		    var s = Substr(dsPerioExam4.GetColumn(nRow,"TIME_REC"),2,2);
		    var t_t = (toNumber(m) * 60) + toNumber(s) + toNumber(dsPerioExam4.GetColumn(nRow,"ERROR_REC"));
			var t_c = Truncate(t_t/60);
			var t_d = t_t%60;
			if (t_d == 0){t_d = "00";
			}else if(t_d < 10){
				t_d = "0"+t_d;
			}
			iRec = toString(t_c)+toString(t_d);
			dsPerioExam4.SetColumn(nRow,"TOT_TIME_REC",iRec); 
			
			for(i=0;i<dsSeltChng.RowCount();i++){
			var iStartRec = toNumber(dsSeltChng.GetColumn(i, "START_REC")); 
			var iEndRec = toNumber(dsSeltChng.GetColumn(i, "END_REC"));
			if( (iRec >= iStartRec)&&(iRec <=iEndRec)){
				iScr = dsSeltChng.getColumn(i,"BAS_SCR");
			}
		}
			dsPerioExam4.SetColumn(nRow,"TIME_SCR",iScr); 
			break;
		default : break;
	}	
}
function dsPerioExam2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
		switch(strColumnID){
			case "LESSON1" :	
			case "LESSON2" :	
			case "LESSON3" :	
			case "LESSON4" :	fcAvgScr(obj,nRow); break;
			default :break;
			}
}

