function fcPassYn(){
	var iCnt = dsPerioExam3.RowCount();
	var iCycleCnt = toNumber(dsUseGbnItem.GetColumn(0,"C"));  //경주 및 자전거 분해조립 시험 갯수 
	var iCycYnCnt = 0;
	var i_PassCnt = 0;
	var i_Pass = "";

	for(var  i=0; i<iCnt; i++){
	
		iCycYnCnt = 0;
		i_HealCnt = 0;
		i_Pass = "";
		// 자전거
		if(!fnc_isNull(dsPerioExam1.GetColumn(i,"SCR_200"))){
			if((dsPerioExam1.GetColumn(i,"SCR_200")) >=60){
				iCycYnCnt++;
			}}
		if(!fnc_isNull(dsPerioExam1.GetColumn(i,"SCR_333"))){
			if((dsPerioExam1.GetColumn(i,"SCR_333")) >=60){
				iCycYnCnt++;
			}}
		if(!fnc_isNull(dsPerioExam1.GetColumn(i,"SCR_500"))){
			if((dsPerioExam1.GetColumn(i,"SCR_500")) >=60){
				iCycYnCnt++;
			}}
		if(!fnc_isNull(dsPerioExam1.GetColumn(i,"SCR_1000"))){
			if((dsPerioExam1.GetColumn(i,"SCR_1000")) >=60){
				iCycYnCnt++;
			}}
		if(!fnc_isNull(dsPerioExam1.GetColumn(i,"SCR_2000"))){
			if((dsPerioExam1.GetColumn(i,"SCR_2000")) >=60){
				iCycYnCnt++;
			}}
		if(!fnc_isNull(dsPerioExam4.GetColumn(i,"TIME_REC"))){
			if((dsPerioExam4.GetColumn(i,"TIME_SCR")) >=60){
				iCycYnCnt++;
			}}	
		if((dsPerioExam2.GetColumn(i,"LESSON1")) >=60){
				iCycYnCnt++;
		}
		if((dsPerioExam2.GetColumn(i,"LESSON2")) >=60){
				iCycYnCnt++;
		}
		if((dsPerioExam2.GetColumn(i,"LESSON3")) >=60){
				iCycYnCnt++;
		}
		if((dsPerioExam2.GetColumn(i,"LESSON4")) >=60){
				iCycYnCnt++;
		}
	
		if(iCycleCnt+4 == iCycYnCnt){
				i_Pass = "합격";
		}else{
				i_Pass = "불합격";
		}
		dsPerioExam3.SetColumn(i,"SUCCESS_YN",i_Pass);
	} //for
}
