			 sRptNm    = "d02000040_6.jrf";          //레포트 파일명
			 sDataSet  = " dsPerioExam5=dsPerioExam5 ";    //"UBI 쿼리명, MIP Dataset명"
			 sArg      = "";
			 sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
			 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}
}
