/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsPerioExam1.ClearData();
    dsPerioExam2.ClearData();
    dsPerioExam3.ClearData();
    dsPerioExamSum.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);
	
	if ( fnc_IsNull(cbPerioNo.Value) ) {
        //조회할 자료가 없음
		fnc_Message(SNIS_CAN_0008, "N", "기수");
		return false;
	}
	
	return true;
}

function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;
    
    var sServiceName  = "d02000041-service:searchPerioExam";
    var sInDataSet    = "";
    var sOutDataSet   = "dsPerioExam1=dsPerioExam1 "
                      + "dsPerioExam2=dsPerioExam2 "
                      + "dsPerioExam3=dsPerioExam3 "
                      + "dsPerioExamSum=dsPerioExamSum ";
    
    fcd_AddParam("RACER_PERIO_NO", cbPerioNo.Value);
    
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

    if (    !fnc_DatasetChangeCheck(dsPerioExam1  )
         && !fnc_DatasetChangeCheck(dsPerioExam2  )
         && !fnc_DatasetChangeCheck(dsPerioExam3  )) 
    {
        //저장 할 자료가 없음
        fnc_Message(SNIS_COM_1006, "N");
        return;
    }

	return true;	
}

/* 
  *EXAM_CD (GRP_CD : D10007  /  CD : 009) 선수자격
      
      * ITEM_GBN_CD 1 (GRP_CD : D10027 / CD : 003 ) 자전거실기
          * ITEM_CD 1 (GRP_CD : D10028 / CD : 001 ) 200 M
          * ITEM_CD 2 (GRP_CD : D10028 / CD : 002 ) 333 M
          * ITEM_CD 3 (GRP_CD : D10028 / CD : 004 ) 1000 M
          * ITEM CD 4 (GRP_CD : D10028 / CD : 000 ) 자전거실기 결과(합격여부)
      
      * ITEM_GBN_CD 2 (GRP_CD : D10027 / CD : 001 ) 학과
          * ITEM_CD 1 (GRP_CD : D10030 / CD : 101 ) 경륜관계법규
          * ITEM_CD 2 (GRP_CD : D10030 / CD : 105 ) 일반상식
          * ITEM_CD 3 (GRP_CD : D10030 / CD : 106 ) 스포츠 의학
          * ITEM_CD 4 (GRP_CD : D10030 / CD : 107 ) 자전거 이론
          * ITEM_CD 5 (GRP_CD : D10030 / CD : 108 ) 분해조립
          * ITEM_CD 6 (GRP_CD : D10030 / CD : 000 ) 필기 결과(합격여부)
      
      * ITEM_GBN_CD 3 (GRP_CD : D10027 / CD : 006 ) 면접
          * ITEM_CD 1 (GRP_CD : D10065 / CD : 221 ) 면접 (합격여부)
          
      * ITEM_GBN_CD 4 (GRP_CD :  / CD : 000 ) 선수자격 결과 
          * ITEM_CD 1 (GRP_CD :  / CD : 000 ) 선수자격 합격 여부 
*/
function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
        
    var sServiceName  = "d02000041-service:savePerioExam";
    var sInDataSet    = "dsPerioExam1=dsPerioExam1:u "
                      + "dsPerioExam2=dsPerioExam2:u "
                      + "dsPerioExam3=dsPerioExam3:u "
                      + "dsPerioExamSum=dsPerioExamSum:u ";
    var sOutDataSet   = "dsPerioExam1=dsPerioExam1 "
                      + "dsPerioExam2=dsPerioExam2 "
                      + "dsPerioExam3=dsPerioExam3 "
                      + "dsPerioExamSum=dsPerioExamSum ";

    fcd_AddParam("RACER_PERIO_NO", cbPerioNo.Value);

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
 	if(tabMain.TabIndex == 0){
		fnc_OpenExcelExport("tabMain.tbExam1.grdPerioExam1");
	}else if(tabMain.TabIndex == 1){
		fnc_OpenExcelExport("tabMain.tbExam2.grdPerioExam2");
	}else if(tabMain.TabIndex == 2){
		fnc_OpenExcelExport("tabMain.tbExam3.grdPerioExam3");
	}else if(tabMain.TabIndex == 3){
		fnc_OpenExcelExport("tabMain.tbExamSum.grdPerioExamSum");
	}
}

