>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}


/*-------------------------------------------------------------------------------------------------+
>>  점수가 바뀐경우
+-------------------------------------------------------------------------------------------------*/
function common_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
    var nExam = substr(obj.id, length("dsPerioExam"));
    
    var nSumRow = fnc_GetExistValue(dsPerioExamSum, "CAND_NO", obj.GetColumn(nRow, "CAND_NO"));
    switch(obj.id) {
        case "dsPerioExam1" :
            var sValue = iif(dsPerioExam1.GetColumn(nRow,'EXAM_REC_1')>'001300'||dsPerioExam1.GetColumn(nRow,'EXAM_REC_2')>'002200'||dsPerioExam1.GetColumn(nRow,'EXAM_REC_4')>'024000'||dsPerioExam1.GetColumn(nRow,'EXAM_REC_6')>'300000','불합격',iif(fnc_IsNull(dsPerioExam1.GetColumn(nRow,'EXAM_REC_1'))||fnc_IsNull(dsPerioExam1.GetColumn(nRow,'EXAM_REC_2'))||fnc_IsNull(dsPerioExam1.GetColumn(nRow,'EXAM_REC_4'))||fnc_IsNull(dsPerioExam1.GetColumn(nRow,'EXAM_REC_6')),'','합격'));
            dsPerioExamSum.SetColumn(nSumRow, "EXAM_" + nExam, sValue);
            break;
        case "dsPerioExam2" :
            var sValue = iif(dsPerioExam2.GetColumn(nRow,'EXAM_SCR_1')<60||dsPerioExam2.GetColumn(nRow,'EXAM_SCR_2')<60||dsPerioExam2.GetColumn(nRow,'EXAM_SCR_3')<60||dsPerioExam2.GetColumn(nRow,'EXAM_SCR_4')<60,'불합격',iif(fnc_IsNull(dsPerioExam2.GetColumn(nRow,'EXAM_SCR_1'))||fnc_IsNull(dsPerioExam2.GetColumn(nRow,'EXAM_SCR_2'))||fnc_IsNull(dsPerioExam2.GetColumn(nRow,'EXAM_SCR_3'))||fnc_IsNull(dsPerioExam2.GetColumn(nRow,'EXAM_SCR_4')),'','합격'));
            dsPerioExamSum.SetColumn(nSumRow, "EXAM_" + nExam, sValue);
            break;
        case "dsPerioExam3" :
            var sValue = iif(dsPerioExam3.GetColumn(nRow,'EXAM_SCR_1')<12||dsPerioExam3.GetColumn(nRow,'EXAM_SCR_2')<12||dsPerioExam3.GetColumn(nRow,'EXAM_SCR_3')<12||dsPerioExam3.GetColumn(nRow,'EXAM_SCR_4')<12||dsPerioExam3.GetColumn(nRow,'EXAM_SCR_5')<12,'불합격',iif(fnc_IsNull(dsPerioExam3.GetColumn(nRow,'EXAM_SCR_1'))||fnc_IsNull(dsPerioExam3.GetColumn(nRow,'EXAM_SCR_2'))||fnc_IsNull(dsPerioExam3.GetColumn(nRow,'EXAM_SCR_3'))||fnc_IsNull(dsPerioExam3.GetColumn(nRow,'EXAM_SCR_4'))||fnc_isNull(dsPerioExam3.GetColumn(nRow,'EXAM_SCR_5')),'','합격'));
            dsPerioExamSum.SetColumn(nSumRow, "EXAM_" + nExam, sValue);
            break;
    }
}

function fcSetExam()
{
    for ( var nRow = 0; nRow < dsPerioExamSum.GetRowCount(); nRow++ ) {
        var sValue = iif(dsPerioExam1.GetColumn(nRow,'EXAM_REC_1')>'001300'||dsPerioExam1.GetColumn(nRow,'EXAM_REC_2')>'002200'||dsPerioExam1.GetColumn(nRow,'EXAM_REC_4')>'024000'||dsPerioExam1.GetColumn(nRow,'EXAM_REC_6')>'300000','불합격',iif(fnc_IsNull(dsPerioExam1.GetColumn(nRow,'EXAM_REC_1'))||fnc_IsNull(dsPerioExam1.GetColumn(nRow,'EXAM_REC_2'))||fnc_IsNull(dsPerioExam1.GetColumn(nRow,'EXAM_REC_4'))||fnc_IsNull(dsPerioExam1.GetColumn(nRow,'EXAM_REC_6')),'','합격'));
        dsPerioExamSum.SetColumn(nRow, "EXAM_1", sValue);
        var sValue = iif(dsPerioExam2.GetColumn(nRow,'EXAM_SCR_1')<60||dsPerioExam2.GetColumn(nRow,'EXAM_SCR_2')<60||dsPerioExam2.GetColumn(nRow,'EXAM_SCR_3')<60||dsPerioExam2.GetColumn(nRow,'EXAM_SCR_4')<60,'불합격',iif(fnc_IsNull(dsPerioExam2.GetColumn(nRow,'EXAM_SCR_1'))||fnc_IsNull(dsPerioExam2.GetColumn(nRow,'EXAM_SCR_2'))||fnc_IsNull(dsPerioExam2.GetColumn(nRow,'EXAM_SCR_3'))||fnc_IsNull(dsPerioExam2.GetColumn(nRow,'EXAM_SCR_4')),'','합격'));
        dsPerioExamSum.SetColumn(nRow, "EXAM_2", sValue);
        var sValue = iif(dsPerioExam3.GetColumn(nRow,'EXAM_SCR_1')<12||dsPerioExam3.GetColumn(nRow,'EXAM_SCR_2')<12||dsPerioExam3.GetColumn(nRow,'EXAM_SCR_3')<12||dsPerioExam3.GetColumn(nRow,'EXAM_SCR_4')<12||dsPerioExam3.GetColumn(nRow,'EXAM_SCR_5')<12,'불합격',iif(fnc_IsNull(dsPerioExam3.GetColumn(nRow,'EXAM_SCR_1'))||fnc_IsNull(dsPerioExam3.GetColumn(nRow,'EXAM_SCR_2'))||fnc_IsNull(dsPerioExam3.GetColumn(nRow,'EXAM_SCR_3'))||fnc_IsNull(dsPerioExam3.GetColumn(nRow,'EXAM_SCR_4'))||fnc_isNull(dsPerioExam3.GetColumn(nRow,'EXAM_SCR_5')),'','합격'));
        dsPerioExamSum.SetColumn(nRow, "EXAM_3", sValue);
    }
}
function fcPrint(){
	
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "";          //레포트 파일명
	var sDataSet  = "";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "";
	var sRptDrct  = 0; 
	
	if (tabMain.TabIndex == 0){ 
		for(var i=0;i<dsPerioExam1.rowCount();i++){
			 if(dsPerioExam1.GetColumn(i,'EXAM_REC_1')>'001300'||dsPerioExam1.GetColumn(i,'EXAM_REC_2')>'002200'||dsPerioExam1.GetColumn(i,'EXAM_REC_4')>'024000'||dsPerioExam1.GetColumn(i,'EXAM_REC_6')>'300000'){
				dsPerioExam1.SetColumn(i,"PASS_YN","불합격");
			 }else{
				dsPerioExam1.SetColumn(i,"PASS_YN","합격");
			 }
			 if(fnc_IsNull(dsPerioExam1.GetColumn(i,'EXAM_REC_1')) && fnc_IsNull(dsPerioExam1.GetColumn(i,'EXAM_REC_2')) && fnc_IsNull(dsPerioExam1.GetColumn(i,'EXAM_REC_4')) && fnc_IsNull(dsPerioExam1.GetColumn(i,'EXAM_REC_6'))){
				dsPerioExam1.SetColumn(i,"PASS_YN","");	
			 }
		}
		 sRptNm    = "d02000041_1.jrf";          //레포트 파일명
		 sDataSet  = " dsPerioExam1=dsPerioExam1 ";    //"UBI 쿼리명, MIP Dataset명"
		 sArg      = "PerioNo="+cbPerioNo.Value;
		 sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
		 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}else if(tabMain.TabIndex == 1){ 
		for(var i=0;i<dsPerioExam2.rowCount();i++){
			 if(dsPerioExam2.GetColumn(i,'EXAM_SCR_1')<60||dsPerioExam2.GetColumn(i,'EXAM_SCR_2')<60||dsPerioExam2.GetColumn(i,'EXAM_SCR_3')<60||dsPerioExam2.GetColumn(i,'EXAM_SCR_4')<60){
				dsPerioExam2.SetColumn(i,"PASS_YN","불합격");
			 }else{
				dsPerioExam2.SetColumn(i,"PASS_YN","합격");
			 }
			 if(fnc_IsNull(dsPerioExam2.GetColumn(i,'EXAM_SCR_1')) && fnc_IsNull(dsPerioExam2.GetColumn(i,'EXAM_SCR_2')) && fnc_IsNull(dsPerioExam2.GetColumn(i,'EXAM_SCR_3')) && fnc_IsNull(dsPerioExam2.GetColumn(i,'EXAM_SCR_4'))){
				dsPerioExam2.SetColumn(i,"PASS_YN","");	
			 }
		}
		
		 sRptNm    = "d02000041_2.jrf";          //레포트 파일명
		 sDataSet  = " dsPerioExam2=dsPerioExam2 ";    //"UBI 쿼리명, MIP Dataset명"
		 sArg      = "PerioNo="+cbPerioNo.Value;
		 sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
		 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}else if(tabMain.TabIndex == 2){ 
		for(var i=0;i<dsPerioExam3.rowCount();i++){
			 if(dsPerioExam3.GetColumn(i,'EXAM_SCR_1')<12||dsPerioExam3.GetColumn(i,'EXAM_SCR_2')<12||dsPerioExam3.GetColumn(i,'EXAM_SCR_3')<12||dsPerioExam3.GetColumn(i,'EXAM_SCR_4')<12||dsPerioExam3.GetColumn(i,'EXAM_SCR_5')<12){
				dsPerioExam3.SetColumn(i,"PASS_YN","불합격");
			 }else{
				dsPerioExam3.SetColumn(i,"PASS_YN","합격");
			 }
			 if(fnc_IsNull(dsPerioExam3.GetColumn(i,'EXAM_SCR_1')) && fnc_IsNull(dsPerioExam3.GetColumn(i,'EXAM_SCR_2')) && fnc_IsNull(dsPerioExam3.GetColumn(i,'EXAM_SCR_3')) && fnc_IsNull(dsPerioExam3.GetColumn(i,'EXAM_SCR_4')) && fnc_IsNull(dsPerioExam2.GetColumn(i,'EXAM_SCR_5'))){
				dsPerioExam3.SetColumn(i,"PASS_YN","");	
			 }
		}
		 sRptNm    = "d02000041_3.jrf";          //레포트 파일명
		 sDataSet  = " dsPerioExam3=dsPerioExam3 ";    //"UBI 쿼리명, MIP Dataset명"
		 sArg      = "PerioNo="+cbPerioNo.Value;
		 sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
		 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}else if(tabMain.TabIndex == 3){ 
		for(var i=0;i<dsPerioExamSum.rowCount();i++){
			 if(dsPerioExamSum.GetColumn(i,'EXAM_1')<60||dsPerioExamSum.GetColumn(i,'EXAM_2')<60||dsPerioExamSum.GetColumn(i,'EXAM_6')<60){
				dsPerioExamSum.SetColumn(i,"PASS_YN","불합격");
			 }else{
				dsPerioExamSum.SetColumn(i,"PASS_YN","합격");
			 }
			 if(fnc_IsNull(dsPerioExamSum.GetColumn(i,'EXAM_1')) || fnc_IsNull(dsPerioExamSum.GetColumn(i,'EXAM_2')) || fnc_IsNull(dsPerioExamSum.GetColumn(i,'EXAM_3'))){
				dsPerioExamSum.SetColumn(i,"PASS_YN","");	
			 }
		}
		 sRptNm    = "d02000041_4.jrf";          //레포트 파일명
		 sDataSet  = " dsPerioExamSum=dsPerioExamSum ";    //"UBI 쿼리명, MIP Dataset명"
		 sArg      = "PerioNo="+cbPerioNo.Value;
		 sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
		 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}
}
