/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'F'+sInpt+'FTTT';
    
 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);
	
	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);
	
	// 화면 초기화
	fcInit();
	
	//공통코드 조회
	//fcd_GetComboDs(dsInComCd);
	       	
	//콤보 설정
	fcSetCombo();
	
	// 조회조건 설정
	/*
	cbPerioNo.Enable      = true;

    cbRound.Enable        = true;
    cbBeforeRound.Enable  = false;
    cbCurrentRound.Enable = false;
    */
    cbPerioNo.Index = 0;
    cbRound.Index = 0;
    cbBeforeRound.Index = 0;
    cbCurrentRound.Index = 1;
}
