/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);
	
	if(fnc_IsNull(cbPerioNo.Value)) {
        
        //조회할 자료가 없음
		fnc_Message(SNIS_CAN_0008, "N", "기수");
		return false;
	}
	
	if(tabMain.TabIndex == 0) {
        if(fnc_IsNull(cbRound.Value)) {
            
            //조회할 자료가 없음
            fnc_Message(SNIS_CAN_0008, "N", "회차");
            return false;
        }
	}
	else if(tabMain.TabIndex == 1) {
        if(fnc_IsNull(cbBeforeRound)) {
            fnc_Message(SNIS_CAN_0008, "N", "전회차");
            return false;
        }
        
        if(fnc_IsNull(cbCurrentRound)) {
            fnc_Message(SNIS_CAN_0008, "N", "현재회차");
            return false;
        }
        // 전회차가 현재회차를 초과할 경우
        if(toInteger(cbBeforeRound.Value) > toInteger(cbCurrentRound.Value)) {
            fnc_Message(SNIS_CAN_M006, "N");
            return false;
        }
	}
	
	return true;
}

function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;

        var sServiceName  = "d02000042-service:searchPerioExam1";
        var sInDataSet    = "";
        var sOutDataSet   = " dsPerioExam1=dsPerioExam1 dsPerioExam2=dsPerioExam2 dsPerioExam3=dsPerioExam3 ";

        fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));  
        fcd_AddParam("examCd", Trim(cbRound.Value));     
        fcd_AddParam("bf_examCd", Trim(cbBeforeRound.Value));
        fcd_AddParam("cur_examCd", Trim(cbCurrentRound.Value));
        
        fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
    //변경된 자료가 있는지 체크
	var oDataSet			= "";
	var sMandatoryColID   	= "";
	var sMandatoryColName 	= "";
	var oGrid             	= "";

	if(tabMain.TabIndex == 0) {
		
		if ( !fnc_DatasetChangeCheck(dsPerioExam1) ) 
		{
            //저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");	
			return;
		}
						
		// 필수항목체크
		oDataSet 			= dsPerioExam1;
		sMandatoryColID 	= "CAND_NO|NM_KOR";
		sMandatoryColName 	= "학번|성명";
		oGrid				= tabMain.tab1.grdPerioExam1;
		
		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
			
	// 기수 체크
	switch(cbPerioNo.Text) {
        
        case null :
        case ""   :
        case " "  :
        case "  " : fnc_Message(SNIS_COM_1035, "N", "기수");
                    return;
            break;
        default   :
            break;
    }
    
    // 회차 체크
    if((cbRound.Value == null) || (cbRound.Value == "")) {
        fnc_Message(SNIS_CAN_0008, "N", "회차");
        return false;
	}
	
	// 일자 체크
	if((calDate.Value == null) || (calDate.Value == "")) {
        fnc_Message(SNIS_CAN_0008, "N", "일자");
        return false;
	}
	}
			
	return true;
}

// 저장전 설정
function fcSaveSet() {
    
    if(tabMain.TabIndex == 0) {
        var curDt = null;
        if(cbRound.Value == "001") {
            if(!fnc_IsNull(calFstDt.Value)) {
                curDt = calFstDt.Value;
            }
            else {
                fnc_Message(SNIS_CAN_0008, "N", "1차의 일자");
                calFstDt.SetFocus();
                return false;
            }
        }
        else if(cbRound.Value == "002") {
            if(!fnc_IsNull(calSndDt.Value)) {
                curDt = calSndDt.Value;
            }
            else {
                fnc_Message(SNIS_CAN_0008, "N", "2차의 일자");
                calSndDt.SetFocus();
                return false;
            }
        }
        else if(cbRound.Value == "003") {
            if(!fnc_IsNull(calTrdDt.Value)) {
                curDt = calTrdDt.Value;
            }
            else {
                fnc_Message(SNIS_CAN_0008, "N", "3차의 일자");
                calTrdDt.SetFocus();
                return false;
            }
        }
                       
        for(var i=0; i<dsPerioExam1.rowcount; i++) {
            dsPerioExam1.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
			dsPerioExam1.SetColumn(i, "EXAM_CD", "011");	
			dsPerioExam1.SetColumn(i, "ITEM_GBN_CD", "011");
            dsPerioExam1.SetColumn(i, "DT", curDt);
            dsPerioExam1.SetColumn(i, "HGHT_SCR", toInteger(cbRound.Value));
            dsPerioExam1.SetColumn(i, "WGHT_SCR", toInteger(cbRound.Value));
            dsPerioExam1.SetColumn(i, "BDYMSS_IDX_SCR", toInteger(cbRound.Value));
            dsPerioExam1.SetColumn(i, "FAT_RATE_SCR", toInteger(cbRound.Value));
            dsPerioExam1.SetColumn(i, "MUS_RATE_SCR", toInteger(cbRound.Value));
            dsPerioExam1.SetColumn(i, "BELLY_FAT_SCR", toInteger(cbRound.Value));
        }
 //       fnc_DatasetPrint(dsPerioExam1);
        return true;
    }
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
       
        
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";
    
    if(tabMain.TabIndex == 0) {
		
		sServiceName  = "d02000042-service:saveBodyCheck";
		sInDataSet    = " dsPerioExam1=dsPerioExam1:u ";
		sOutDataSet   = " dsPerioExam1=dsPerioExam1 ";
			
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	}
}
    
