function fcToExcel(obj) {
    if(tabMain.TabIndex == 0) {
        fnc_OpenExcelExport("tabMain.tab1.grdPerioExam1");
    }
    else if(tabMain.TabIndex == 1) {
        fnc_OpenExcelExport("tabMain.tab2.grdPerioExam2");
    }
    else if(tabMain.TabIndex == 2) {
        fnc_OpenExcelExport("tabMain.tab3.grdPerioExam3");
    }
}

