/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
   
   if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
   switch(sServiceName) {
       case "d02000042-service:searchPerioExam1" : 
              fcSetAvg();   
            break;
       case "d02000042-service:saveBodyCheck" :
			fcSearch();      
            break;
       default :
       
           break;
   }
    
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

