// 평균 설정
function fcSetAvg() {
    if(dsPerioExam3.rowcount > 0) {
        for(var i=0; i<dsPerioExam3.rowcount; i++) {
            var cnt = 0;
            var avg = 0.0;
            
            var first_HGHT    = dsPerioExam3.GetColumn(i,"FST_HGHT");
            var second_HGHT   = dsPerioExam3.GetColumn(i,"SND_HGHT");
            var third_HGHT    = dsPerioExam3.GetColumn(i,"TRD_HGHT");
                        
            var first_WGHT    = dsPerioExam3.GetColumn(i,"FST_WGHT");
            var second_WGHT   = dsPerioExam3.GetColumn(i,"SND_WGHT");
            var third_WGHT    = dsPerioExam3.GetColumn(i,"TRD_WGHT");
            
            
            var first_BDYMSS  = dsPerioExam3.GetColumn(i,"FST_BDYMSS_IDX");
            var second_BDYMSS = dsPerioExam3.GetColumn(i,"SND_BDYMSS_IDX");
            var third_BDYMSS  = dsPerioExam3.GetColumn(i,"TRD_BDYMSS_IDX");
            
            var first_FAT     = dsPerioExam3.GetColumn(i,"FST_FAT_RATE");
            var second_FAT    = dsPerioExam3.GetColumn(i,"SND_FAT_RATE");
            var third_FAT     = dsPerioExam3.GetColumn(i,"TRD_FAT_RATE");
            
            var first_MUS     = dsPerioExam3.GetColumn(i,"FST_MUS_RATE");
            var second_MUS    = dsPerioExam3.GetColumn(i,"SND_MUS_RATE");
            var third_MUS     = dsPerioExam3.GetColumn(i,"TRD_MUS_RATE");
            
            var first_BELLY   = dsPerioExam3.GetColumn(i,"FST_BELLY_FAT");
            var second_BELLY  = dsPerioExam3.GetColumn(i,"SND_BELLY_FAT");
            var third_BELLY   = dsPerioExam3.GetColumn(i,"TRD_BELLY_FAT");
            
            // 신장 평균
            if(!fnc_IsNull(first_HGHT))  { cnt ++; }
            if(!fnc_IsNull(second_HGHT)) { cnt ++; }
            if(!fnc_IsNull(third_HGHT))  { cnt ++; }
            
            avg = (
                    (toNumber(SubStr(first_HGHT, 0, 3))+toNumber(SubStr(second_HGHT, 0, 3))+toNumber(SubStr(third_HGHT, 0, 3)))
                    +(toNumber("0."+SubStr(first_HGHT, 3, 1))+toNumber("0."+SubStr(second_HGHT, 3, 1))+toNumber("0."+SubStr(third_HGHT, 3, 1)))
                  ) / cnt;
            dsPerioExam3.SetColumn(i,"AVG_HGHT", round(avg,1));
            
            // 체중 평균
            cnt = 0;
            avg = 0.0;
            if(!fnc_IsNull(third_HGHT))  { cnt ++; }
            if(!fnc_IsNull(second_WGHT)) { cnt ++; }
            if(!fnc_IsNull(third_WGHT))  { cnt ++; }
            avg = (
                    (toNumber(SubStr(first_WGHT, 0, 2))+toNumber(SubStr(second_WGHT, 0, 2))+toNumber(SubStr(third_WGHT, 0, 2)))
                    +(toNumber("0."+SubStr(first_WGHT, 2, 1))+toNumber("0."+SubStr(second_WGHT, 2, 1))+toNumber("0."+SubStr(third_WGHT, 2, 1)))
                  ) / cnt;
            dsPerioExam3.SetColumn(i,"AVG_WGHT", round(avg,1));
            
            // 체질량 지수 평균
            cnt = 0;
            avg = 0.0;
            if(!fnc_IsNull(first_BDYMSS))   { cnt ++; }
            if(!fnc_IsNull(second_BDYMSS))  { cnt ++; }
            if(!fnc_IsNull(third_BDYMSS))   { cnt ++; }
            avg = (
                    (toNumber(SubStr(first_BDYMSS, 0, 2))+toNumber(SubStr(second_BDYMSS, 0, 2))+toNumber(SubStr(third_BDYMSS, 0, 2)))
                    +(toNumber("0."+SubStr(first_BDYMSS, 2, 1))+toNumber("0."+SubStr(second_BDYMSS, 2, 1))+toNumber("0."+SubStr(third_BDYMSS, 2, 1)))
                  ) / cnt;
            dsPerioExam3.SetColumn(i,"AVG_BDYMSS_IDX", round(avg,1));
            
            // 체지방률 평균
            cnt = 0;
            avg = 0.0;
            if(!fnc_IsNull(first_FAT))   { cnt ++; }
            if(!fnc_IsNull(second_FAT))  { cnt ++; }
            if(!fnc_IsNull(third_FAT))   { cnt ++; }
            avg = (
                    (toNumber(SubStr(first_FAT, 0, 2))+toNumber(SubStr(second_FAT, 0, 2))+toNumber(SubStr(third_FAT, 0, 2)))
                    +(toNumber("0."+SubStr(first_FAT, 2, 1))+toNumber("0."+SubStr(second_FAT, 2, 1))+toNumber("0."+SubStr(third_FAT, 2, 1)))
                  ) / cnt;
            dsPerioExam3.SetColumn(i,"AVG_FAT_RATE", round(avg,1)); 
                
            // 근육량 평균
            cnt = 0;
            avg = 0.0;
            if(!fnc_IsNull(first_MUS)){
                cnt ++;
            }
            if(!fnc_IsNull(second_MUS)){
                cnt ++;
            }
            if(!fnc_IsNull(third_MUS)){
                cnt ++;
            }
            avg = (
                    (toNumber(SubStr(first_MUS, 0, 2))+toNumber(SubStr(second_MUS, 0, 2))+toNumber(SubStr(third_MUS, 0, 2)))
                    +(toNumber("0."+SubStr(first_MUS, 2, 1))+toNumber("0."+SubStr(second_MUS, 2, 1))+toNumber("0."+SubStr(third_MUS, 2, 1)))
                  ) / cnt;
            dsPerioExam3.SetColumn(i,"AVG_MUS_RATE", round(avg,1));
                
            // 복부비만
            cnt = 0;
            avg = 0.0;
            if(!fnc_IsNull(first_BELLY)){
                cnt ++;
            }
            if(!fnc_IsNull(second_BELLY)){
                cnt ++;
            }
            if(!fnc_IsNull(third_BELLY)){
                cnt ++;
            }
            avg = (
                    (toNumber(SubStr(first_BELLY, 0, 2))+toNumber(SubStr(second_BELLY, 0, 2))+toNumber(SubStr(third_BELLY, 0, 2)))
                    +(toNumber("0."+SubStr(first_BELLY, 2, 2))+toNumber("0."+SubStr(second_BELLY, 2, 2))+toNumber("0."+SubStr(third_BELLY, 2, 2)))
                  ) / cnt;
            dsPerioExam3.SetColumn(i,"AVG_BELLY_FAT", round(avg,2)); 
        }
	}
}

function fcPrint(){

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "";          //레포트 파일명
	var sDataSet  = "";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "";
	var sRptDrct  = 0;   
	if (tabMain.TabIndex == 0){ 
		 sRptNm    = "d02000042_1.jrf";          //레포트 파일명
		 sDataSet  = " dsPerioExam1=dsPerioExam1 ";    //"UBI 쿼리명, MIP Dataset명"
		 sArg      = "Round="+cbRound.Value;
		 sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
		 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	
	}else if (tabMain.TabIndex == 1){ 
		 sRptNm    = "d02000042_2.jrf";          //레포트 파일명
		 sDataSet  = " dsPerioExam2=dsPerioExam2 ";    //"UBI 쿼리명, MIP Dataset명"
		 sArg      = "Round1="+cbBeforeRound.Text;
		 sArg     += " Round2="+cbCurrentRound.Text;         
		 sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
		 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	
	}else if (tabMain.TabIndex == 2){ 
		 sRptNm    = "d02000042_3.jrf";          //레포트 파일명
		 sDataSet  = " dsPerioExam3=dsPerioExam3 ";    //"UBI 쿼리명, MIP Dataset명"
		 sArg      = "";       
		 sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
		 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	
	}
}
