/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
   
   if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
   	
   switch(sServiceName) {
       case "d02000043-service:searchPerioNo" :
			cbPerioNo.Index = 0;
			cbRound.Index = 0;
			cbBeforeRound.Index = 0;
			cbCurrentRound.Index = 1;
			break;
       case "d02000043-service:searchPerioExam1" :
              fcSetAvg();                
            break;
       case "d02000043-service:searchPerioExam2":
                               
            break;
           
       case "d02000043-service:searchPerioExam3":
               
            break;     
       
       case "d02000043-service:saveBodyCheck" :
                    
            break;
       default :
       
           break;
   }
    
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

