                    +(toInteger(SubStr(secondREC2000, 4, 2)))
                    +(toInteger(SubStr(thirdREC2000, 4, 2)))
                    +(toInteger(SubStr(endREC2000, 4, 2)))
                  ) / cnt;  // 평균 밀리초
            
            minVal  = toInteger(avg/(100*60));         // 분
            secVal  = toInteger((avg%(100*60))/100);   // 초 
            mileVal = toInteger((avg%(100*60))%100);   // 밀리초
            
            strMinVal = toString(minVal);
            strSecVal = toString(secVal);
            strMileVal= toString(mileVal);
            
            if(bound(strMinVal) == 1)  { strMinVal  = ("0"+strMinVal);  }
            if(bound(strSecVal) == 1)  { strSecVal  = ("0"+strSecVal);  }
            if(bound(strMileVal) == 1) { strMileVal = ("0"+strMileVal); }
            
            strAvg = (strMinVal + ":" + strSecVal + ":" + strMileVal);
            if(strAvg == "00:00:00") { strAvg = ""; }
                            
            dsPerioExam3.SetColumn(i, "AVG_REC_2000", strAvg);
            
            cnt     = 0;
            avg     = 0;
            minVal  = 0;
            secVal  = 0;
            mileVal = 0;
            strAvg     = null;
            strMinVal  = null;
            strSecVal  = null;
            strMileVal = null;
        }
	}
}


// TAB 변경에 따른 조회조건 처리
function tabMain_OnChanged(obj,nOldIndex,nNewindex)
{
	if(nNewindex == 0) { 
        cbPerioNo.Enable      = true;
        cbRound.Enable        = true;
       
        cbBeforeRound.Enable  = false;
        cbCurrentRound.Enable = false;
    }
	else if(nNewindex == 1){
        cbPerioNo.Enable      = true;
        cbRound.Enable        = false;
        
        cbBeforeRound.Enable  = true;
        cbCurrentRound.Enable = true;
    }
	else if(nNewindex == 2) {
        cbPerioNo.Enable      = true;
        cbRound.Enable        = false;
        
        cbBeforeRound.Enable  = false;
        cbCurrentRound.Enable = false;
    }
}

function fcPrint(){
	
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     			
	var sRptNm    = "";          
	var sDataSet  = "";   
	var sArg      = "";
	var sRptDrct  = 0;   
	if (tabMain.TabIndex == 0){ 
			 sRptNm    = "d02000043_1.jrf";         
			 sDataSet  = " dsPerioExam1=dsPerioExam1 ";    
			 sArg      = "PerioNo="+cbPerioNo.Value+" Round="+cbRound.Text;
			 sRptDrct  = 1;                               
			 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}else if (tabMain.TabIndex == 1){ 
			 sRptNm    = "d02000043_2.jrf";         
			 sDataSet  = " dsPerioExam2=dsPerioExam2 ";    
			 sArg      = "Round1="+cbBeforeRound.Text;
			 sArg     += " Round2="+cbCurrentRound.Text;  			 
			 sRptDrct  = 1;                               	
			 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);		 
	}else if (tabMain.TabIndex == 2){ 
			 sRptNm    = "d02000043_3.jrf";          
			 sDataSet  = " dsPerioExam3=dsPerioExam3 ";    
			 sArg      = "PerioNo="+cbPerioNo.Value;			 
			 sRptDrct  = 1;                               	
			 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);		 
	}
	
}
