function fcSearchCheck(obj) {

    if( fnc_IsNull(cbSearchPerioNo.Value) ) {
        fnc_Message(SNIS_CAN_0008, "N", "기수");
        return false;
    }
        if( fnc_IsNull(cbSelectRec.Value) ) {
        fnc_Message(SNIS_CAN_0008, "N", "구분");
        return false;
    }
	return true;
}
