function fcSearch(obj) {
 
   if ( !fcSearchCheck(obj) ) return;
	
	 fcSearchUseGbnItem();  //2008.05.26 update
}
function fcUseEditable(){

		for (var i=0;i<dsUseItem.RowCount();i++){  
			var itemCode = dsUseItem.GetColumn(i,"ITEM_CD");
			switch(itemCode){
			   case "301" :
				tabMain.tab1.grdCycleRec.SetCellProp("Body",3,"Edit","mask");
				break;
			   case "302" :
				tabMain.tab1.grdCycleRec.SetCellProp("Body",5,"Edit","mask");
				break;
			   case "303" :
				tabMain.tab1.grdCycleRec.SetCellProp("Body",7,"Edit","mask");
				break;
			   case "304" :
				tabMain.tab1.grdCycleRec.SetCellProp("Body",9,"Edit","mask");
				break;
			   case "305" :
				tabMain.tab1.grdCycleRec.SetCellProp("Body",11,"Edit","mask");
				break;	
			}
		}
}
function dsCycleRec_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var iRec = ToFloat(varNewValue);
	var iScr = 0;
	switch(strColumnID){
		case "REC_200"  : dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='301'"); break;
		case "REC_333"  : dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='302'"); break;
		case "REC_500"  : dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='303'"); break;
		case "REC_1000" : dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='304'"); break;
		case "REC_2000" : dsSeltChng.Filter("ITEM_GBN_CD = '003' AND ITEM_CD ='305'"); break;
	default : break;
	}
	
	for(i=0;i<dsSeltChng.RowCount();i++){
			var iStartRec = toNumber(dsSeltChng.GetColumn(i, "START_REC")); 
			var iEndRec = toNumber(dsSeltChng.GetColumn(i, "END_REC"));
			if( (iRec >= iStartRec)&&(iRec <=iEndRec)){
				 iScr = dsSeltChng.getColumn(i,"BAS_SCR");
			 }
		}
	switch(strColumnID){
		case "REC_200"  : dsCycleRec.SetColumn(nRow,"SCR_200",iScr);   break;
		case "REC_333"  : dsCycleRec.SetColumn(nRow,"SCR_333",iScr);   break;
		case "REC_500"  : dsCycleRec.SetColumn(nRow,"SCR_500",iScr);   break;
		case "REC_1000" : dsCycleRec.SetColumn(nRow,"SCR_1000",iScr);  break;
		case "REC_2000" : dsCycleRec.SetColumn(nRow,"SCR_2000",iScr);  break;
	default : break;
	}
	fcAvgScr(obj, nRow);// 평균,환산점수
}

function fcAvgScr(obj, nRow) {

	var scrCnt = 0;
	var totScr = 0;
	var avgScr = 0;    
	
	for(var i=0; i<dsCycleRec.rowcount; i++) {  
		if(i == nRow) {
			if(toNumber(dsCycleRec.GetColumn(i,"REC_200")) > 0) {
				scrCnt++;
				totScr += ToInteger(dsCycleRec.GetColumn(i,"SCR_200"));
			}
			if(toNumber(dsCycleRec.GetColumn(i,"REC_333")) > 0) {
				scrCnt++;
				totScr += ToInteger(dsCycleRec.GetColumn(i,"SCR_333"));
			}
			if(toNumber(dsCycleRec.GetColumn(i,"REC_500")) > 0) {
				scrCnt++;
				totScr += ToInteger(dsCycleRec.GetColumn(i,"SCR_500"));
			}
			if(toNumber(dsCycleRec.GetColumn(i,"REC_1000")) > 0) {
				scrCnt++;
				totScr += ToInteger(dsCycleRec.GetColumn(i,"SCR_1000"));
			}				
			if(toNumber(dsCycleRec.GetColumn(i,"REC_2000")) > 0) {
				scrCnt++;
				totScr += ToInteger(dsCycleRec.GetColumn(i,"SCR_2000"));
			}
			avgScr = Round(ToNumber(totScr / scrCnt),2);
			dsCycleRec.SetColumn(i,"AVG_SCR",avgScr); //평균점수 셋팅
			break;
		}
	}
}
