	sServiceName     = "d02000044-service:savecyclerec";
	sInDataSet       = "dsCycleRec=dsCycleRec:u ";
	sOutDataSet      = "dsCycleRec=dsCycleRec " ;

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
	
}
