}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if (!fnc_IsMandatory(cboStndYear, "년도")) return false;
   if (!fnc_IsMandatory(cbFromRound, "회차")) return false;
   if (!fnc_IsMandatory(cbToRound, "회차")) return false;
   if (!fnc_IsMandatory(edtDefaultRound, "기본출주회차")) return false;
   return true;
}

//조회 
function fcSearch(obj) {
	if (!fcSearchCheck()) return;
    
    var sServiceName = "d02000052-service:searchCUpDownLvl";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutCUpDownLvl=dsOutCUpDownLvl";
    
    fcd_AddParam("RACE_YY", cboStndYear.Value);
    fcd_AddParam("FROM_ROUND", cbFromRound.Value);
    fcd_AddParam("TO_ROUND", cbToRound.Value);
    fcd_AddParam("DEFAULT_ROUND", edtDefaultRound.Text);	//기본출주회수
	fcd_AddParam("LOWEST_POINT", -6);						//최하위점수 (O등급 점수 - 2 * 3)
	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutCUpDownLvl))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	
	// 필수항목체크
	oDataSet          = dsOutCUpDownLvl;
	sMandatoryColID   = "RACE_YY|GRD";
	sMandatoryColName = "년도|선수등급";
	oGrid             = grdPageList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	return true;
}

