/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	if (dsOutCUpDownLvl.ColCount() > 0) {
		var nRow = dsOutCUpDownLvl.AddRow();        	  //Record Add
		cboiStndYear.SetFocus();
	} else {
		fnc_Message(SNIS_COM_0002, "N");      //DataSet의 Header가 없어서 오류
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( !fnc_DeleteCheck(dsOutCUpDownLvl, "CHK", "1") ) return;
	grdPageList.SetFocus();                                  //Record 삭제 후 Grid로 Focus

}

