/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
	// 조회 후
	if ( sServiceName == "d02000052-service:searchCUpDownLvl" ) {
	    if(dsOutCUpDownLvl.GetRowCount() > 0){
			fnc_SetStatus(SNIS_CAN_0003, dsOutCUpDownLvl.GetRowCount());
	    }
	}
	// 저장 후
	if ( sServiceName == "d02000052-service:saveCUpDownLvl" ) {
		if(dsOutCUpDownLvl.GetRowCount() > 0){
			fnc_SetStatus(SNIS_CAN_0003, dsOutCUpDownLvl.GetRowCount());
	    }
	}
	
	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

