//row 변경시 
function dsOutCUpDownLvl_OnRowPosChanged(obj,nOldRow,nRow)
{
	LATEST_ROW_POS = nOldRow;
}


//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutCUpDownLvl_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fnc_ProgressClose(); //팝업창이 Close안되었으면 Close시킨다.
	if(LATEST_ROW_POS > 0) dsOutCUpDownLvl.Row = LATEST_ROW_POS;
	dsOutCUpDownLvl_OnRowPosChanged(obj, LATEST_ROW_POS, LATEST_ROW_POS);
}

//년도 변경시 회차 조회
function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcd_GetRound(strCode);
}

function dsYear_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fcd_GetRound(cboStndYear.Value);
}
