/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
   
    if ( obj.id == "btnCancel" || obj.id == "btnSearch" ) {
    
        dsOutPlaceList.ClearData();
        fcd_GetComboDs(dsInComCd);
    }    
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearchCheck(obj) {

	fcInit(obj);
	
	if( fnc_IsNull(cbPerioNo.Value) ) {
        // 기수 누락
        fnc_Message(SNIS_CAN_0008, "N", "기수");
        return false;
    }
   
	return true;
}


function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;
    
    dsPlaceList.ClearData();
    	        
    var sServiceName = "d02009005-service:searchReceiptStatus";
    var sInDataSet   = "";
    var sOutDataSet  = "dsReceipt1=dsReceipt1 dsReceipt2=dsReceipt2 dsReceipt3=dsReceipt3 dsReceipt4=dsReceipt4 dsReceipt5=dsReceipt5" ;
              	  	              	  	
	fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
    var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "d02009005.jrf";          //레포트 파일명
	var sDataSet  = " dsReceipt1=dsReceipt1 dsReceipt2=dsReceipt2 dsReceipt3=dsReceipt3 dsReceipt4=dsReceipt4 dsReceipt5=dsReceipt5 ";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "PERIO_NO="+cbPerioNo.Value;
	
	var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
	
	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}

