/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
    switch(sServiceName) {
    
        // 공통코드 조회시
        case "d02009005-service:searchPerioNo" :
            fcSearch();           
            break;
        
        // 검색조건으로 조회했을 경우
        case "d02009005-service:searchPlaceList" :
            fnc_DatasetPrint(dsPlaceList);
            fcGetOutList();               
            break;
        case "d02009005-service:searchPlaceList2" :
            break;               
        default :
            break;
    }
    
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

