/*-------------------------------------------------------------------------------------------------+
>>  인원수 필터링, 데이터 셋 설정후 출력
+-------------------------------------------------------------------------------------------------*/
function fcGetOutList(obj) {
    
   if(dsPlaceList.rowcount > 0) {
      
      var seoulCnt    = 0;  // 서울 출신자 수
      var daejeonCnt  = 0;  // 대전 출신자 수
      var incheonCnt  = 0;  // 인천 출신자 수
      var daeguCnt    = 0;  // 대구 출신자 수
      var ulsanCnt    = 0;  // 울산 출신자 수
      var busanCnt    = 0;  // 부산 출신자 수
      var kwangjuCnt  = 0;  // 광주 출신자 수
      var kyungiCnt   = 0;  // 경기 출신자 수
      var kangwonCnt  = 0;  // 강원 출신자 수
      var chungbukCnt = 0;  // 충북 출신자 수
      var chungnamCnt = 0;  // 충남 출신자 수
      var jeonbukCnt  = 0;  // 전북 출신자 수
      var jeonnamCnt  = 0;  // 전남 출신자 수
      var kyungbukCnt = 0;  // 경북 출신자 수 
      var kyungnamCnt = 0;  // 경남 출신자 수
      var jaejuCnt    = 0;  // 제주 출신자 수
      for(var i=0; i<dsPlaceList.rowcount; i++) {
             
          if(pos(dsPlaceList.GetColumn(i,"ADDR"), "서울") >= 0 ) {
              seoulCnt ++;
          }
          else if(pos(dsPlaceList.GetColumn(i,"ADDR"), "인천") >= 0) {
              incheonCnt ++;
          }
          else if(pos(dsPlaceList.GetColumn(i,"ADDR"), "대전") >= 0) {
              daejeonCnt ++;
          }
          else if(pos(dsPlaceList.GetColumn(i,"ADDR"), "대구") >= 0) {
              daeguCnt ++;
          }
          else if(pos(dsPlaceList.GetColumn(i,"ADDR"), "울산") >= 0) {
              ulsanCnt ++;
          }
          else if(pos(dsPlaceList.GetColumn(i,"ADDR"), "부산") >= 0) {
              busanCnt ++;
          }
          else if(pos(dsPlaceList.GetColumn(i,"ADDR"), "광주") >= 0) {
              kwangjuCnt ++;
          }
          else if(pos(dsPlaceList.GetColumn(i,"ADDR"), "경기") >= 0) {
              kyungiCnt ++;
          }
          else if(pos(dsPlaceList.GetColumn(i,"ADDR"), "강원") >= 0) {
              kangwonCnt ++;
          }
          else if(pos(dsPlaceList.GetColumn(i,"ADDR"), "충북") >= 0) {
              chungbukCnt ++;
          }
          else if(pos(dsPlaceList.GetColumn(i,"ADDR"), "충남") >= 0) {
              chungnamCnt ++;
          }
          else if(pos(dsPlaceList.GetColumn(i,"ADDR"), "전북") >= 0) {
              jeonbukCnt ++;
          }
          else if(pos(dsPlaceList.GetColumn(i,"ADDR"), "전남") >= 0) {
              jeonnamCnt ++;
          }
          else if(pos(dsPlaceList.GetColumn(i,"ADDR"), "경북") >= 0) {
              kyungbukCnt ++;
          }
          else if(pos(dsPlaceList.GetColumn(i,"ADDR"), "경남") >= 0) {
              kyungnamCnt ++;
          }
          else if(pos(dsPlaceList.GetColumn(i,"ADDR"), "제주") >= 0) {
              jaejuCnt ++;
          }
          
      }
      
      dsPlaceList.ClearData();
      dsOutPlaceList.ClearData();
      
      // 화면출력 작업(지역명, 인원수)
      for(var i=0; i<16; i++) {
          dsOutPlaceList.AddRow();
          switch(i) {
              case 0 : 
                  dsOutPlaceList.SetColumn(i, "PLC", "001");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", seoulCnt);
                break;
              case 1 :
                  dsOutPlaceList.SetColumn(i, "PLC", "002");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", incheonCnt);
                break;
              case 2 :
                  dsOutPlaceList.SetColumn(i, "PLC", "003");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", daejeonCnt);
                break;
              case 3 :
                  dsOutPlaceList.SetColumn(i, "PLC", "004");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", daeguCnt);
                break;
              case 4 : 
                  dsOutPlaceList.SetColumn(i, "PLC", "005");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", ulsanCnt);
                break;
              case 5 : 
                  dsOutPlaceList.SetColumn(i, "PLC", "006");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", busanCnt);
                break;
              case 6 : 
                  dsOutPlaceList.SetColumn(i, "PLC", "007");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", kwangjuCnt);
                break;
              case 7 : 
                  dsOutPlaceList.SetColumn(i, "PLC", "008");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", kyungiCnt);
                break;
              case 8 : 
                  dsOutPlaceList.SetColumn(i, "PLC", "009");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", kangwonCnt);
                break;
              case 9 : 
                  dsOutPlaceList.SetColumn(i, "PLC", "010");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", chungbukCnt);
                break;
              case 10 : 
                  dsOutPlaceList.SetColumn(i, "PLC", "011");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", chungnamCnt);
                break;
              case 11 : 
                  dsOutPlaceList.SetColumn(i, "PLC", "012");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", jeonbukCnt);
                break;
              case 12 : 
                  dsOutPlaceList.SetColumn(i, "PLC", "013");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", jeonnamCnt);
                break;
              case 13 : 
                  dsOutPlaceList.SetColumn(i, "PLC", "014");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", kyungbukCnt);
                break;
              case 14 : 
                  dsOutPlaceList.SetColumn(i, "PLC", "015");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", kyungnamCnt);
                break;
              case 15 : 
                  dsOutPlaceList.SetColumn(i, "PLC", "016");
                  dsOutPlaceList.SetColumn(i, "PER_NUM", jaejuCnt);
                break;
              default :
                break;
          }          
          
      } 
   }
   else {
      fnc_Message(SNIS_CAN_0010, "N", "해당 데이터");  
   }
}

