/*-------------------------------------------------------------------------------------------------+
>>  Clear 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
 
  if ( obj.id == "btnCancel" || obj.id == "btnSearch") {  
        dsEquipDetl.ClearData();      
    }    
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearchCheck(obj) {
    
	fcInit(obj);
	
	//if(fnc_IsNull(cbEquip.Value)) {
        
        //조회할 자료가 없음
		//fnc_Message(SNIS_CAN_0008, "N");
		//return false;
	//}
	if (calEntDd1.Value != "" && calEntDd2.Value != "") {
		if (calEntDd1.Text > calEntDd2.Text) {
			fnc_Message(SNIS_CAN_D001, "N","입고일");
			return false;
		}
	}
	
	if (calEntDd1.Value == "") {
    //  fnc_Message(SNIS_CAN_D001, "N","입고일자 시작일자");
    //  return false;
		calEntDd1.Text = '00000000';
    }
    
    if (calEntDd2.Value == "") {
    //  fnc_Message(SNIS_CAN_D001, "N","입고일자 끝일자");
    //  return false;
		calEntDd2.Text = '99999999';
    }
	
	return true;
}
//조회버튼 클릭 시
function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;
    
    var sServiceName = "";
    var sInDataSet   = ""; 
    var sOutDataSet  = "";  
     
   
    // 장비명으로  테이블을 조회하여 그리드에 출력한다.	
    
   
    if (cbEquip.text != "" && cbEquip.text != "전체") {     
      sServiceName = "d03000002-service:searchEquipDetl1";
     fcd_AddParam("equipCd", cbEquip.Value);  
	 fcd_AddParam("entDd1", calEntDd1.Value); 
	 fcd_AddParam("entDd2", calEntDd2.Value); 
	}
	else {	 
	  sServiceName = "d03000002-service:searchEquipDetl2";
	  fcd_AddParam("entDd1", calEntDd1.Value); 
	  fcd_AddParam("entDd2", calEntDd2.Value); 	
	}
	
	sOutDataSet  = "dsEquipDetl=dsEquipDetl";
	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {
   
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsEquipDetl))     
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	
	//입력 오류 체크 
	var nRowCnt     = dsEquipDetl.GetRowCount();  //행의 갯수 	
	
	for (var i=0; i < nRowCnt; i++) {
	 
	    if (dsEquipDetl.GetColumn(i,"EQUIP_CD") == NULL) {
	    
	        fnc_Message(SNIS_CAN_D002, "N",i+1 + "행의 장비명");
	        return false;
	    }
	    
	    if (dsEquipDetl.GetColumn(i,"ENT_DD") == NULL) {
	    
	        fnc_Message(SNIS_CAN_D002, "N",i+1 + "행의 입고일");
	        return false;
	    }   
	    if (dsEquipDetl.GetColumn(i,"ENT_DD") <= "20080127") {
	        fnc_Message(SNIS_CAN_D003, "N",i+1 + "행의 입고일");
	        return false;
	    }
	    
	    if (dsEquipDetl.GetColumn(i,"SUPR") == NULL) {
	    
	        fnc_Message(SNIS_CAN_D002, "N",i+1 + "행의 공급자");
	        return false;
	    }
	    
	    if (dsEquipDetl.GetColumn(i,"QTY") == NULL) {
	    
	        fnc_Message(SNIS_CAN_D002, "N",i+1 + "행의 수량");
	        return false;
	    }
	    
	    if (dsEquipDetl.GetColumn(i,"QTY") < 1 ) {
	    
	       fnc_Message(SNIS_CAN_D004, "N",i+1,"수량");
	        return false;
	    }
	    
	    if (dsEquipDetl.GetColumn(i,"STAT") == NULL) {
	    
	        fnc_Message(SNIS_CAN_D002, "N",i+1 + "행의 상태");
	        return false;
	    }
	}  
	 
	 
	// 그리드에서 장비명과 입고일이 동일한 것이 있는지 체크 시작 // 
	var nRow  = 0;
	if ( nRowCnt >= 2) {
	  for (var i=0; i < nRowCnt; i++) {	      
	   for (var i=0; i < nRowCnt; i++) {
	     if ( (dsEquipDetl.GetColumn(nRow,"EQUIP_CD") == dsEquipDetl.GetColumn(nRow+i+1,"EQUIP_CD"))
	          && (dsEquipDetl.GetColumn(nRow,"ENT_DD") == dsEquipDetl.GetColumn(nRow+i+1,"ENT_DD")))
	     {
	        fnc_Message(SNIS_CAN_D007, "N",nRow+1,nRow+i+2);
	        return false;	     
	     } // inner if end		    
	   } // inner for end
	   nRow++;
	  } //  outer for end  
	} // outer if end
   //  그리드에서 장비명과 입고일이 동일한 것이 있는지 체크 끝 //    
	
    
	return true;

}



// 저장 공통
function fcSave(obj) {
  
  if ( !fcSaveCheck() ) return;
   
  
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
       
    sServiceName     = "d03000002-service:saveEquipDetl";    
    sInDataSet       = "dsEquipDetl=dsEquipDetl:u " ;    
    sOutDataSet      = "dsEquipDetl=dsEquipDetl " ;

    
    fcd_AddParam("entDd", dsEquipDetl.GetColumn(0,"ENT_DD"));
    fcd_AddParam("equipCd", dsEquipDetl.GetColumn(0,"EQUIP_CD"));  
 
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


