/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
    switch(sServiceName) {
    
        // 공통코드 조회시
        case "sServiceName == common-service:searchCode" :
           
            fcSearch("1");
            
            break;
	       
	     //장비명 콤보에 전체라는 임시 레코드를 추가한다
	     // 전체 조회용으로  사용  
         case "d03000002-service:searchEquip"    : 
               dsEquipSet.InsertRow(0);
	           dsEquipSet.SetColumn(0, "CD_NM", "전체");         
               break;
       
         case "d03000002-service:searchEquipDetl1"  :  //장비내역 조회1
                fnc_setstatus(SNIS_CAN_0003, dsEquipDetl.GetRowCount());               
             break;   
             
         case "d03000002-service:searchEquipDetl2"  :  //장비내역 조회2 
               fnc_setstatus(SNIS_CAN_0003, dsEquipDetl.GetRowCount());                
             break;   
               
         case "d03000002-service:saveEquipDetl"  :  //장비내역 저장

             break;
              
         default :
        
            break;
    }
    
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

