function fcSearchCheck(obj) {

	fcInit(obj);
	
	if( fnc_IsNull(cbPerioNo.Value) ) {
        // ±â¼ö ´©¶ô
        fnc_Message(SNIS_CAN_0008, "N", "±â¼ö");
        return false;
    }
            		
	return true;
}

