/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
    switch(sServiceName) {
    
        // 콤보 조회후
        case "d03000017-service:searchPerioNo" :
            cbPerioNo.Index = 0;
            fcSearch();
            break;
      
        
        // 결석사항 조회후
        case "d03000017-service:searchEduAbsList" :
            //fnc_DatasetPrint(dsYearList);           
            fcTransferDataSet();    
            break;               
                       
        default :
            break;
    }
    
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

// 기본교육시간을 출력
function fcTransferDataSet() {
    
    var numberVal = 0;
    var countVal = 0;
    var eduTime = 0;
    var countColumn = dsYearList.rowcount / dsYearList.GetColumn(0, "CAND_COUNT");
     
    if( dsYearList.rowcount > 0) {
		for(var y=5; y<countColumn + 5; y++) {
			grdAbsList.SetSubCellProp("Head", y, 0,"Text", substr(dsYearList.GetColumn(y-5,"YY"),0,4)+"/"+substr(dsYearList.GetColumn(y-5,"YY"),4,2));
			grdAbsList.SetSubCellProp("Head", y, 1,"Text", dsYearList.GetColumn(y-5,"EDU_TIME"));
		}
		for(var y=countColumn + 5; y<18; y++) {
			grdAbsList.SetSubCellProp("Head", y, 0,"Text", "");
			grdAbsList.SetSubCellProp("Head", y, 1,"Text", "");
		}
    }
    
    //  일인당 기본데이터 카운트 구하기
    if( dsEduTime.rowcount >0 ) {
        
        for(var i=0; i<dsEduTime.rowcount; i++) {   
            if(dsEduTime.GetColumn(0, "CAND_NO") == dsEduTime.GetColumn(i, "CAND_NO")) {
                
                numberVal ++;
            }    
        }
     }   
        // 대상자 카운트 구하기
     countVal = (dsEduTime.rowcount) / numberVal;
           
     dsOutAbsList.ClearData();
     
     // 대상자 카운트만큼 후보생 번호, 이름 출력 
     if(countVal > 0) {
         var k=0;    
         for(var i=0; i<countVal; i++) {
                
            // 대상자 수만큼 row 생성
            dsOutAbsList.AddRow();
            dsOutAbsList.SetColumn(i, "CAND_NO", dsEduTime.GetColumn(k, "CAND_NO"));
            dsOutAbsList.SetColumn(i, "NM_KOR", dsEduTime.GetColumn(k, "NM_KOR"));
              
            k = k + numberVal;
         }   
      }      
      
      //  교육이수현황 설정
      for(var j=0; j<dsEduTime.rowcount; j++) {
          var candNo = Trim(dsEduTime.GetColumn(j, "CAND_NO"));                                                                           
          var dt =  Trim(dsEduTime.GetColumn(j, "DT")); 
             
          for(var s=0; s<dsOutAbsList.rowcount; s++) {
                                
              if(Trim(dsOutAbsList.GetColumn(s, "CAND_NO")) == candNo) {
					for(var m=1; m<countColumn + 1; m++) {
						if(Trim(dsYearList.GetColumn(m-1,"YY")) == dt) {
							dsOutAbsList.SetColumn(s, "M"+m, dsEduTime.GetColumn(j,"EDU_TIME"));                     
                        }
                    }
              }
          }  
      }
            
      //  교육훈련일지 결석사유 오전/오후별 설정            
      for(var j=0; j<dsAbsList.rowcount; j++) {
          var candNo = Trim(dsAbsList.GetColumn(j, "CAND_NO")); 
          var dt =  Trim(dsAbsList.GetColumn(j, "DT")); 
             
          for(var s=0; s<dsOutAbsList.rowcount; s++) {
                               
              if(Trim(dsOutAbsList.GetColumn(s, "CAND_NO")) == candNo) {
                  for(var m=1; m<countColumn + 1; m++) {
						if(Trim(dsYearList.GetColumn(m-1,"YY")) == dt) {
							dsOutAbsList.SetColumn(s, "M"+m, toInteger(dsOutAbsList.GetColumn(s,"M"+m)) - toInteger(dsAbsList.GetColumn(j,"EDU_TIME")));                     
                        }
                    }           
              }
          }       
      }
      
      for(var s=0; s<dsOutAbsList.rowcount; s++) {
            for(var j=0; j<dsEduTime.rowcount; j++) {
                 if(Trim(dsOutAbsList.GetColumn(s, "CAND_NO")) == Trim(dsEduTime.GetColumn(j, "CAND_NO"))) {
					 eduTime = eduTime + ToInteger(dsEduTime.GetColumn(j,"EDU_TIME"));  
                 }
            }
            for(var j=0; j<dsAbsList.rowcount; j++) {
                 if(Trim(dsOutAbsList.GetColumn(s, "CAND_NO")) == Trim(dsAbsList.GetColumn(j, "CAND_NO"))) {
					 eduTime = eduTime - ToInteger(dsAbsList.GetColumn(j,"EDU_TIME")); 
                 }
            }
            dsOutAbsList.SetColumn(s, "TOT_TIME", eduTime); 
            dsOutAbsList.SetColumn(s, "REST_TIME", dsEduTime.GetColumn(s,"TOT_EDU_TIME") - eduTime);
            eduTime =0;
      }
}


