function fcPrint(){

	var Argu_ym = "";
    for (var i=0;i<13;i++){
		Argu_ym = Argu_ym+" M"+(i+1)+"="+dsYearList.getcolumn(i,"YY")+"00";
    }
    //alert(Argu_ym);
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "d03000017.jrf";          //레포트 파일명
	var sDataSet  = "dsOutAbsList=dsOutAbsList";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "PerioNo="+cbPerioNo.Value+Argu_ym;

	var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
	
	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	
}
