/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	
	// 필수항목체크
	var sMandatoryColID   	= null;
	var sMandatoryColName 	= null;
	var oGrid             	= null;
	var oDataSet			= null;
	
	if(length(sRacerPerioNo) <= 0 || length(sCandNo) <= 0 ){	
		//기수,후보생번호 필수 저장
		fnc_Message(SNIS_CAN_A013, "N");
		return;
	}
	
	if(tabMain.TabIndex == 0){
		if ( !fnc_DatasetChangeCheck(dsHealth) ) 
		{
			//저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");	
			return;
		}	
		oDataSet 		= dsHealth;
		sMandatoryColID   = "DT|IJRY_DETL|CURE_GBN|CUR_CNTNT";
		sMandatoryColName = "일짜|부상내역|치료구분|치료내용";
		oGrid             = tabMain.tab1.grdHealth;
		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}		
		
	}else if(tabMain.TabIndex == 1){
		if(length(tabMain.tab2.edWoundDt.Text) <= 0){	
			//일자 필수 저장
			fnc_Message(SNIS_CAN_A003, "N");
			return;
		}			
	}
		
	return true;
}


function fcSave(obj) {
    
   if ( !fcSaveCheck() ) return;
    
//    fcSaveSetting();
           
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
    //alert("sRacerPerioNo===>"+sRacerPerioNo);   
	if(tabMain.TabIndex == 0){ //개인상태
	    sServiceName  = "d04000001-service:saveHealth";
		sInDataSet    = "dsHealth=dsHealth:u ";
		sOutDataSet   = "dsHealth=dsHealth ";
		//fnc_DatasetPrint(dsHealth);
		for(var i=0; i<dsHealth.rowcount; i++) {  
			dsHealth.SetColumn(i, "RACER_PERIO_NO", sRacerPerioNo);
			dsHealth.SetColumn(i, "CAND_NO", sCandNo);
	
			if(toupper(dsHealth.GetRowType(i)) == 'INSERT' || toupper(dsHealth.GetRowType(i)) == 'UPDATE' ){
				saveFile(i);//파일저장
			}
		}
		//fnc_DatasetPrint(dsHealth);
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);				
	}else if(tabMain.TabIndex == 1){ //신체부위		
		//신체부위정보1(체크박스) 
	    sServiceName  = "d04000001-service:saveWound1";
		sInDataSet    = "dsWound1=dsWound1:u ";
		sOutDataSet   = "dsWound1=dsWound1 ";
		for(var i=0; i<dsWound1.rowcount; i++) {  
			dsWound1.SetColumn(i, "RACER_PERIO_NO", sRacerPerioNo);
			dsWound1.SetColumn(i, "CAND_NO", sCandNo);
			dsWound1.SetColumn(i, "DT", tabMain.tab2.edWoundDt.value);
		}
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);			
	}
}

function fcSaveWound2() {
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
    
	//신체부위정보2
	sServiceName  = "d04000001-service:saveWound2";
