}

//파일저장
function saveFile(iRow){
	if( length(dsHealth.GetColumn(iRow, "DIAG_FILE_REAL_NM" )) > 0 ) {
		var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
									, dsHealth.GetColumn(iRow, "DIAG_FILE_REAL_NM" )
									, dsHealth.GetColumn(iRow, "DIAG_FILE_DISP_NM" ) );
		
		var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsHealth.GetColumn(iRow, "DIAG_FILE_REAL_NM" ));


		dsHealth.SetColumn(iRow, "DIAG_FILE_REAL_NM" , rtn_arr[1] );//실제 파일 경로.
	} 
}


