/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	var sDt = "";
	switch(sServiceName) {
		case "d04000001-service:saveHealth" :
			//fnc_DatasetPrint(dsHealth);
	        break;
        // 신체부위정보1 조회 콜백
        case "d04000001-service:searchWoundCheck1" :         
            fcSearchWound2();
            break;
        // 신체부위정보1 저장 콜백
        case "d04000001-service:saveWound1" :
            fcSaveWound2();
            break;  
        // 신체부위정보2 조회 콜백
        case "d04000001-service:searchWoundList2" :
			wound_display_color();
          	for(var i=0; i<dsWound2.rowcount; i++) { 
				sDt = dsWound2.GetColumn(i, "DT");
				break;
			}
			tabMain.tab2.edWoundDt.value = sDt;
            break;    
        case "d04000001-service:searchWoundCheck1_2" :
            wound_display_color();    
            break;                     
		default :
			break;
	}
    
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

