}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리
	if ( obj.GetForm().id == "divBtnRow" ) {
		if(tabMain.TabIndex == 0){ //개인상태
			dsHealth.AddRow();
		}
    }        
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( obj.GetForm().id == "divBtnRow" ) {
		if(tabMain.TabIndex == 0){ //개인상태
			if ( !fnc_DeleteCheck(dsHealth, "CHK", "1") ) return;			
			tabMain.tab1.grdHealth.SetFocus();   //Record 삭제 후 Grid로 Focus
		}
		fcInit();		
		break;  	
	} 
}

function fcWound1Add(){
