/*-------------------------------------------------------------------------------------------------+
>>  그리드에서 팝업 처리 
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnExpandEdit(objGrd, nRow, nCell, strVal, nPivotIndex){
	var sColIdx     = objGrd.GetCellText("HEAD", nRow, nCell);  //해당 HEAD명 
	var sSearchKind = "";
	var sInputValue = "";
    var sResponse; 

	switch (sColIdx){
		case "일자" :
			fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
			break;
			
		case "진단서" :   //사진
			sResponse = fcd_GetSelectFile();
			if ( !fnc_IsNull(sResponse) ) {
				dsHealth.SetColumn(dsHealth.currow, "DIAG_FILE_REAL_NM", sResponse[0] + "\\" + sResponse[1]);
				dsHealth.SetColumn(dsHealth.currow, "DIAG_FILE_DISP_NM", sResponse[1]);
			}
			break;	
			
		case "처치사항 및 약품명" :   //신체부위조회
            //           alert("MOVE~~~~~~~~~~~~~~!");
			preDsRowNo   = dsHealth.rowcount;
			preDsCurrow  = nRow; 
			preDsRowType = dsHealth.GetRowType(nRow);
			preObjGrid   = objGrd;    
            
			var sDt = "";
			var sServiceName = "";
			var sInDataSet   = "";
			var sOutDataSet  = "";
			
			for(var i=0; i<dsHealth.rowcount; i++) { 
				if(i == nRow){
					sDt = dsHealth.GetColumn(i, "DT");
					break;
				}
			}
			
			tabMain.tabindex = 1;
			
			//신체부위정보 조회
			sServiceName = "d04000001-service:searchWoundCheck1_2";
			sInDataSet   = "";
			sOutDataSet  = " dsWound1=dsWound1 ";				
			fcd_AddParam("racerPerioNo", sRacerPerioNo);
			fcd_AddParam("candNo", sCandNo);
			fcd_AddParam("dt", sDt);
			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);

			//부상정보 리스트 조회	
			sServiceName = "d04000001-service:searchWoundList2_2";
			sInDataSet   = "";
			sOutDataSet  = " dsWound2=dsWound2 ";	
			fcd_AddParam("racerPerioNo", sRacerPerioNo);
			fcd_AddParam("candNo", sCandNo);
			fcd_AddParam("dt", sDt);
			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);		
					
			tabMain.tab2.edWoundDt.value = sDt;
			break;
							
		default :
			break;	
	}

}


function tabMain_OnChanged(obj,nOldIndex,nNewindex)
{
	//alert("CHANGE~~~~~~~~~~~!");
	// 개인상태에서 신체부위로 탭 이동되었을 경우
	if((nOldIndex == 0) && (nNewindex == 1)) {
        
        if(preObjGrid == "grdHealth") {
            if((preDsRowNo <= 0) || (preDsRowType != "normal")) {
                
                fnc_Message(SNIS_CAN_M008, "N");
                  
                tabMain.TabIndex = 0;
                tabMain.tab1.grdHealth.setFocus();
            }
        }
    }
}

function tabMain_tab1_grdHealth_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sLocalFileName  = fcd_GetSelectFile("Save", dsHealth.GetColumn(nRow, "DIAG_FILE_DISP_NM"));
    var sServerFileName = dsHealth.GetColumn(nRow, "DIAG_FILE_REAL_NM");
    var sFileSize       = 0;
   // alert(sServerFileName);
    if ( sLocalFileName == null ) return;
    
	fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsHealth);

}

