function Button0_OnClick(obj)
{
	for(var i = 0; i < this.Components.count; i++) {
		trace(this.Components[i]);
		if(this.Components[i].GetType() == "Grid") {
			destroy(this.Components[i].id);
		}
	}
	//destroy("grd_1");
	//destroy("grd_3");
}
