/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'T'+sInpt+'FTTT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	//공통코드 조회
	fcd_GetComboDs(dsInComCd);
	tabMain.TabIndex = 0;
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/

function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.

	var sDataset 	 = "dsSeltReptDesc|dsFamList|dsEduList|dsActList|";
		sDataset	+= "dsCarList|dsLicList|dsUploadFileList";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Clear 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	if ( obj.id == "btnCancel" || obj.id == "btnSearch") {
		cbRacerPerioNo.Index = -1;
		dsSeltReptList.ClearData();
		dsSeltReptDesc.ClearData();

		dsFamList.ClearData();
		dsEduList.ClearData();
		dsActList.ClearData();
		dsCarList.ClearData();
		dsLicList.ClearData();
		dsUploadFileList.ClearData();
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj){
    if(fnc_IsNull(cbRacerPerioNo.Value)){
		fnc_Message(SNIS_CAN_0008, "N","기수");
		return;
	}

    var sServiceName = "d06000005-service:searchSeltRept";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSeltReptList=dsSeltReptList";

	fcd_AddParam("RACER_PERIO_NO", Trim(cbRacerPerioNo.Value));
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false, "S");
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
// 저장 공통
function fcSave(obj) {
	var tabTitle;	//탭인경우..

		//변경된 자료가 있는지 체크
		if ( !fnc_DatasetChangeCheck(dsSeltReptDesc) 
		  && !fnc_DatasetChangeCheck(dsFamList) 
		  && !fnc_DatasetChangeCheck(dsEduList) 
		  && !fnc_DatasetChangeCheck(dsActList) 
		  && !fnc_DatasetChangeCheck(dsCarList) 
		  && !fnc_DatasetChangeCheck(dsLicList) 
		  && !fnc_DatasetChangeCheck(dsUploadFileList)) {
			//저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");
			return false;
		}

		// 선발기수 입력항목 체크
		if(dsSeltReptDesc.GetUpdate()){
			if(ToUpper(dsSeltReptDesc.GetRowType(0)) == "INSERT"){
				dsSeltReptDesc.SetColumn(0,"RACER_PERIO_NO",cbRacerPerioNo.Value);
			}

			if (fnc_IsNull(dsSeltReptDesc.GetColumn(0,"RACER_PERIO_NO"))) {
				fnc_Message(SNIS_COM_1034, "N", "선발기수");
				return;
			}

			if(fnc_IsNull(dsSeltReptDesc.GetColumn(0,"NM_KOR"))){
				fnc_Message(SNIS_COM_1034, "N", "성명(한글)");
				return;
			}

			if(fnc_IsNull(dsSeltReptDesc.GetColumn(0,"BIRTH"))){
				fnc_Message(SNIS_COM_1034, "N", "생년월일");
				return;
			}

			if(fnc_IsNull(dsSeltReptDesc.GetColumn(0,"SEX"))){
				fnc_Message(SNIS_COM_1034, "N", "성별");
				return;
			}

			if(fnc_IsNull(dsSeltReptDesc.GetColumn(0,"ADDR"))){
				fnc_Message(SNIS_COM_1034, "N", "주소");
				return;
			}
			if(fnc_IsNull(dsSeltReptDesc.GetColumn(0,"DETL_ADDR"))){
				fnc_Message(SNIS_COM_1034, "N", "상세주소");
				return;
			} 

			if(fnc_IsNull(dsSeltReptDesc.GetColumn(0,"PHONE1"))){
				fnc_Message(SNIS_COM_1034, "N", "전화번호");
				return;
			}

			if(fnc_IsNull(dsSeltReptDesc.GetColumn(0,"PHONE2"))){
				fnc_Message(SNIS_COM_1034, "N", "전화번호");
				return;
			}
			if(fnc_IsNull(dsSeltReptDesc.GetColumn(0,"PHONE3"))){
				fnc_Message(SNIS_COM_1034, "N", "전화번호");
				return;
			} 

			if(fnc_IsNull(dsSeltReptDesc.GetColumn(0,"SPA_PHONE1"))){
				fnc_Message(SNIS_COM_1034, "N", "긴급연락처");
				return;
			}

			if(fnc_IsNull(dsSeltReptDesc.GetColumn(0,"SPA_PHONE2"))){
				fnc_Message(SNIS_COM_1034, "N", "긴급연락처");
				return;
			}
			if(fnc_IsNull(dsSeltReptDesc.GetColumn(0,"SPA_PHONE3"))){
				fnc_Message(SNIS_COM_1034, "N", "긴급연락처");
				return;
			} 

		}

		if(dsFamList.GetUpdate()){//가족사항
			tabTitle = "가족사항";
			for(var i=0;i<dsFamList.GetRowCount();i++){
				if(fnc_IsNull(dsFamList.GetColumn(i,"FMLY_RELN"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "가족관계");
					return;
				}

				if(fnc_IsNull(dsFamList.GetColumn(i,"NM_KOR"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "성명");
					return;
				}
				if(fnc_IsNull(dsFamList.GetColumn(i,"BIRTH_DT"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "생년월일");
					return;
				}
				if(fnc_IsNull(dsFamList.GetColumn(i,"LUNSOL"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "음력/양력");
					return;
				}
				if(fnc_IsNull(dsFamList.GetColumn(i,"LODGE_YN"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "동거여부");
					return;
				}
			}
		}

		if(dsEduList.GetUpdate()){//학력사항
			tabTitle = "학력사항";
			for(var i=0;i<dsEduList.GetRowCount();i++){
				if(fnc_IsNull(dsEduList.GetColumn(i,"MTRIC_SCHL_DT"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "입학일자");
					return;
				}
				if(fnc_IsNull(dsEduList.GetColumn(i,"GRDU_SCHL_DT"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "졸업일자");
					return;
				}
				if(fnc_IsNull(dsEduList.GetColumn(i,"LAST_SCHL_NM"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "출신교");
					return;
				}
				if(fnc_IsNull(dsEduList.GetColumn(i,"MJR"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "학과(계열)");
					return;
				}
				if(fnc_IsNull(dsEduList.GetColumn(i,"GRDU_GBN"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "구분(졸업)");
					return;
				}
				if(fnc_IsNull(dsEduList.GetColumn(i,"THIS_SCHL_GBN"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "구분(본교)");
					return;
				}
				if(fnc_IsNull(dsEduList.GetColumn(i,"DAY_GBN"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "구분(주간)");
					return;
				}
				if(fnc_IsNull(dsEduList.GetColumn(i,"LAST_SCHL_AREA"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "소재지");
					return;
				}
			}
		}

		if(dsActList.GetUpdate()){//주요활동
			tabTitle = "주요활동";
			for(var i=0;i<dsActList.GetRowCount();i++){
				if(fnc_IsNull(dsActList.GetColumn(i,"STR_DT"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "활동기간(시작)");
					return;
				}
				if(fnc_IsNull(dsActList.GetColumn(i,"END_DT"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "활동기간(끝)");
					return;
				}
			}
		}

		if(dsCarList.GetUpdate()){//경력사항
			tabTitle = "경력사항";
			for(var i=0;i<dsCarList.GetRowCount();i++){
				if(fnc_IsNull(dsCarList.GetColumn(i,"STR_DT"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "기간(시작)");
					return;
				}
				if(fnc_IsNull(dsCarList.GetColumn(i,"END_DT"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "기간(끝)");
					return;
				}
				if(fnc_IsNull(dsCarList.GetColumn(i,"CARR_TERM"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "경력기간");
					return;
				}
				if(fnc_IsNull(dsCarList.GetColumn(i,"OFFC_NM"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "근무처");
					return;
				}

			}
		}

		if(dsLicList.GetUpdate()){//자격면허
			tabTitle = "자격면허";
			for(var i=0;i<dsLicList.GetRowCount();i++){
				if(fnc_IsNull(dsLicList.GetColumn(i,"CERT_NM"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "자격(면허)명");
					return;
				}
				if(fnc_IsNull(dsLicList.GetColumn(i,"REG_DT"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "취득일");
					return;
				}
			}
		}

		if(dsUploadFileList.GetUpdate()){//파일첨부
			tabTitle = "파일첨부";
			for(var i=0;i<dsUploadFileList.GetRowCount();i++){
				if(fnc_IsNull(dsUploadFileList.GetColumn(i,"FILE_TITLE"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "파일구분");
					return;
				}
				if(fnc_IsNull(dsUploadFileList.GetColumn(i,"FILE_NAME"))){
					fnc_Message("["+tabTitle+"] 탭의 "+SNIS_COM_1034, "N", "파일");
					return;
				}

			}
		}


		if(ToUpper(dsSeltReptDesc.GetRowType(0)) == "INSERT"){//insert일 경우에만 키를 구한다.
			var sServiceName = "";
			var sInDataSet   = "";
			var sOutDataSet  = "";  

			sServiceName	 = "d06000005-service:keyCandIdent";
			sInDataSet		 = "";
			sOutDataSet		 = "dsCandSeq=dsCandSeq";

            fcd_AddParam("RACER_PERIO_NO", Trim(cbRacerPerioNo.Value));
			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
		}
		else{
			fcSaveProc();
		}



}

function fcSaveProc(nCandNo){
	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	if(Length(nCandNo)>0){//키가 넘어 왔을 경우에만 키를 셋팅. 아닐 경우는 그냥 처리.
		dsSeltReptDesc.SetColumn(0,"REPT_NO",nCandNo);
	}
	sServiceName     = "d06000005-service:saveSeltRept";    

	sInDataSet       = "dsSeltReptDesc=dsSeltReptDesc:A";
	sInDataSet		+= " dsFamList=dsFamList:u";
	sInDataSet		+= " dsEduList=dsEduList:u";
	sInDataSet		+= " dsActList=dsActList:u";
	sInDataSet		+= " dsCarList=dsCarList:u";
	sInDataSet		+= " dsLicList=dsLicList:u";
	sInDataSet		+= " dsUploadFileList=dsUploadFileList:u";


	sOutDataSet      = "";

	if(dsUploadFile.GetRowCount()>0 || dsUploadFileList.GetRowCount() > 0){

		saveFile();//파일저장

		if(dsUploadFile.GetRowCount()>0){
			dsSeltReptDesc.SetColumn(0,"PTO_URL",dsUploadFile.GetColumn(0,"FILE_URL"));
		}
	}

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false, "U");
}

function saveFile(obj){
	if( dsUploadFile.GetRowCount() > 0 ) {
		if(dsUploadFile.getColumn(0, "CHK") == "1" ) {
			var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
										, dsUploadFile.GetColumn(0, "FILE_URL" ) 
										, dsUploadFile.GetColumn(0, "FILE_NAME" )
										, "RACER");

			var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFile.GetColumn(0, "FILE_URL" ));
			//if ( rtn_arr[0] != "SUCC" ) {
			//		alert("첨부파일 업로드 에러발생(사진파일)");
			//		return false;
			//}

			dsUploadFile.SetColumn(0, "FILE_URL"    , rtn_arr[1] );//실제 파일 경로.
			dsUploadFile.SetColumn(0, "FILE_SIZE"   , fileSize 	 );
		} // end if
	} // end if 

	 if( dsUploadFileList.GetRowCount() > 0 ) {
		for(var i=0 ; i < dsUploadFileList.GetRowCount() ; i++){
			if(dsUploadFileList.getColumn(i, "CHK") == "1" ) {
				var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
											, dsUploadFileList.GetColumn(i, "FILE_URL" )
											, dsUploadFileList.GetColumn(i, "FILE_NAME" )
											, "RACER");

				var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFileList.GetColumn(i, "FILE_URL" ));
				//if ( rtn_arr[0] != "SUCC" ) {
				//	alert("첨부파일 업로드 에러발생(파일첨부)");
				//	return false;
				//}

				dsUploadFileList.SetColumn(i, "FILE_URL"    , rtn_arr[1] );//실제 파일 경로.
				dsUploadFileList.SetColumn(i, "FILE_SIZE"   , fileSize );
			}  // end if
		}      // end for
	} 		 

}

/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(){

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "d06000005.jrf";          //레포트 파일명
	var sDataSet  = "dsSeltReptDesc=dsSeltReptDesc";    //"UBI 쿼리명, MIP Dataset명"

	//선수사진경로 가져오기
    var sPtoName = dsSeltReptList.GetColumn(0, "PTO_URL");

    var sPhysicalFileName = split(sPtoName, "//");
    var ptourl = GBL_IMAGELINK_URL + sPhysicalFileName[length(sPhysicalFileName) - 1];

	var sArg  = " IMG_URL="+ptourl; 

	var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}

