/*-------------------------------------------------------------------------------------------------+
>>  Help Popup Open
+-------------------------------------------------------------------------------------------------*/
function fcHelpDialoge(obj,nX,nY) {
	var sSearchKind = "";
	var sInputValue = "";
    var sResponse; 

	switch(obj.id){
		case "imgZip":
			sResponse = fcd_HelpDialoge("POST","","");
			if(Length(sResponse)){
				dsSeltReptDesc.SetColumn(0,"POST_NO1",substr(sResponse[0],0,3));
				dsSeltReptDesc.SetColumn(0,"POST_NO2",substr(sResponse[0],4,3));
				dsSeltReptDesc.SetColumn(0,"ADDR",sResponse[1]);
			}
			break;
		case "imgPerZip":
			sResponse = fcd_HelpDialoge("POST","","");
			if(Length(sResponse)){
				dsSeltReptDesc.SetColumn(0,"PERMNT_POST_NO1",substr(sResponse[0],0,3));
				dsSeltReptDesc.SetColumn(0,"PERMNT_POST_NO2",substr(sResponse[0],4,3));
				dsSeltReptDesc.SetColumn(0,"PERMNT",sResponse[1]);
			}
			break;
	}
}

