/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG){

    if(!fnc_result(ErrorCode, ErrorMSG) ) return;
    switch(sServiceName){

		case "common-service:searchCode" :
			cbRacerPerioNo.Index = 0;
			fcSearch(this);
			break;


        case "d06000005-service:saveSeltRept"  :  //후보생선발접수 저장
			fcSearch();
			break;

		case "d06000005-service:searchSeltRept" :
			if(dsSeltReptList.RowCount() > 0) {
				dsSeltReptList_OnRowPosChanged("","",0);				
			}
			break;

		case "d06000005-service:deleteSeltRept" :
			dsSeltReptDesc.ClearData();
			fcSearch();
			break;

		case "d06000005-service:"://후보생 정보 조회
			MaskEdit4.Readonly = true;
			break;

		case "d06000005-service:keyCandIdent":
			fcSaveProc(dsCandSeq.GetColumn(0,"SEQ"));
			break;

		case "d06000005-service:searchSeltReptDesc":
			//이미지 set
			var img = split(dsSeltReptDesc.GetColumn(0,"PTO_URL"),"/");
			imgPhoto.ImageID = GBL_IMAGELINK_URL+img[Length(img)-1];
			break;
    }

    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

