/*-------------------------------------------------------------------------------------------------+
>>  삭제 처리  
+-------------------------------------------------------------------------------------------------*/
function fcDeleteSeltRept() {
	if(dsSeltReptDesc.GetRowCount() == 0){
		fnc_Message(SNIS_COM_1003,"N");
	}
	else if(confirm("선택된 정보를 삭제하시겠습니까?")){
		dsSeltReptDesc.SetColumn(0,"DEL_YN","Y");

		sServiceName     = "d06000005-service:deleteSeltRept";

		sInDataSet       = "dsSeltReptDesc=dsSeltReptDesc:A";
		sInDataSet		+= " dsFamList=dsFamList:u";
		sInDataSet		+= " dsEduList=dsEduList:u";
		sInDataSet		+= " dsActList=dsActList:u";
		sInDataSet		+= " dsCarList=dsCarList:u";
		sInDataSet		+= " dsLicList=dsLicList:u";
		sInDataSet		+= " dsUploadFileList=dsUploadFileList:u";

		sOutDataSet      = "";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리
	switch(obj.GetForm().id) {
	case "divBtnSelt" :
		if(dsSeltReptDesc.GetRowCount() != 0){
			dsSeltReptDesc.ClearData();
			dsFamList.ClearData();
			dsEduList.ClearData();
			dsActList.ClearData();
			dsCarList.ClearData();
			dsLicList.ClearData();
			dsUploadFileList.ClearData();
		}
		var nRow = dsSeltReptDesc.AddRow();
		dsSeltReptDesc.SetColumn(nRow,"JOIN_PATH","002");
		edSeltNo.Readonly = false;
		break;
	case "divBtnFam":
		if(isAddable()){
			var nRow = dsFamList.AddRow();
		}
		break;
	case "divBtnEdu":
		if(isAddable()){
			var nRow = dsEduList.AddRow();
		}
		break;
	case "divBtnAct":
		if(isAddable()){
			var nRow = dsActList.AddRow();
		}
		break;
	case "divBtnCar":
		if(isAddable()){
			var nRow = dsCarList.AddRow();
		}
		break;
	case "divBtnLic":
		if(isAddable()){
			var nRow = dsLicList.AddRow();
		}
		break;
	case "divBtnFile":
		if(isAddable()){
			fcFileSelect(obj);
		}
		break;	

	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj){
	switch(obj.GetForm().id){
	case "divBtnSelt":
		fcDeleteSeltRept();
		break;
	case "divBtnFam":
		fcDelRowChk(dsFamList);
		break;
	case "divBtnEdu":
		fcDelRowChk(dsEduList);
		break;
	case "divBtnAct":
		fcDelRowChk(dsActList);
		break;
	case "divBtnCar":
		fcDelRowChk(dsCarList);
		break;
	case "divBtnLic":
		fcDelRowChk(dsLicList);
		break;
	case "divBtnFile":
		if ( !fnc_DeleteCheck(dsUploadFileList, "chk", "1") ) return;
		break;	
	}
}

function fcDelRowChk(obj){
	if(obj.currow == -1){
		fnc_Message("선택된 행이 없습니다.","N");
		return;
	}
	else{
		obj.DeleteRow(obj.currow);
	}
}

