function grdViewFile_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sLocalFileName  = fcd_GetSelectFile("Save", dsUploadFileList.GetColumn(nRow, "FILE_NAME"));
    var sServerFileName = dsUploadFileList.GetColumn(nRow, "FILE_URL");
    var sFileSize       = dsUploadFileList.GetColumn(nRow, "FILE_SIZE");
    if ( sLocalFileName == null ) return;

	fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsUploadFileList);

}

