function dsSeltReptList_OnRowPosChanged(obj,nOldRow,nRow)
{
	var sServiceName     = "d06000005-service:searchSeltReptDesc";
	var sInDataSet       = "";
	var sOutDataSet      = "dsSeltReptDesc=dsSeltReptDesc";
		sOutDataSet		+= " dsFamList=dsFamList";
		sOutDataSet		+= " dsEduList=dsEduList";
		sOutDataSet		+= " dsActList=dsActList";
		sOutDataSet		+= " dsCarList=dsCarList";
		sOutDataSet		+= " dsLicList=dsLicList";
		sOutDataSet		+= " dsUploadFileList=dsUploadFileList";

	var sRacerPerioNo    = dsSeltReptList.getColumn(nRow,   "RACER_PERIO_NO");
	var sReptNo          = dsSeltReptList.GetColumn(nRow,   "REPT_NO");


	if((fnc_IsNull(sRacerPerioNo) || fnc_IsNull(sReptNo)) && cbRacerPerioNo.Index != -1){
		fnc_Message("지원자리스트를 찾을 수 없습니다. 확인하시고 처리하시기 바랍니다.","N");

		dsSeltReptDesc.ClearData();
		dsFamList.ClearData();
		dsEduList.ClearData();
		dsActList.ClearData();
		dsCarList.ClearData();
		dsLicList.ClearData();
		dsUploadFileList.ClearData();

		return;
	}

	fcd_AddParam("RACER_PERIO_NO", sRacerPerioNo);  //기수
	fcd_AddParam("REPT_NO",        sReptNo);        //접수번호
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false, "");
}
