/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	switch ( obj.GetForm().id ) {
		case "itemBtnRow" :			
            if ( fnc_DatasetChangeCheck(dsItemCodeListValue) ) {
                fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
                return;
            }

            dsItemCodeListValue.addRow();
			break;
		case "prizBtnRow" :
			var nRow = dsPrizCodeListValue.addRow();			
			dsPrizCodeListValue.FireEvent = false;
			dsPrizCodeListValue.SetColumn(nRow , "ITEM_CD" , 
				dsItemCodeListValue.GetColumn(dsItemCodeListValue.Currow,"ITEM_CD"));
			dsPrizCodeListValue.FireEvent = true;
            break;						
    } // end switch.case
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	switch ( obj.GetForm().id ) {
		case "itemBtnRow" :
            var sArg = "";

			if ( !fnc_DeleteCheck(dsItemCodeListValue, "chk", "1") ) return;
			break;
		case "prizBtnRow" :
			if ( !fnc_DeleteCheck(dsPrizCodeListValue, "chk", "1") ) return;
            break;

    } // end switch.case
}



/*-------------------------------------------------------------------------------------------------+
>>  상벌항목코드 조회
+-------------------------------------------------------------------------------------------------*/
function fcGetPrizCode(sIndex) {
    var sServiceName = "d06000009-service:searchPrizCode";
    var sInDataSet   = "";
    var sOutDataSet  = "dsPrizCodeListValue=dsPrizCodeListValue";

    fcd_AddParam("ITEM_CD", dsItemCodeListValue.getColumn(sIndex, "ITEM_CD"));
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);       
}


function fcGetPrizCodeBefore(obj,nRow,nCell,nX,nY,nPivotIndex){
	fcGetPrizCode(nRow);
}
