function fcInit(obj) {
    if ( obj.id == "btnCancel" || obj.id == "btnSearch" ) {
		if(tabMain.TabIndex == 0){
			dsStrtMesurCd1.ClearData();
		}else if(tabMain.TabIndex == 1){
			dsStrtMesurBas2.ClearData();	
		}else if(tabMain.TabIndex == 2){
			dsStrtMesurRound5.ClearData();	
		}
    }    
}

