/*-------------------------------------------------------------------------------------------------+
>>  그리드에서 팝업 처리 
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnExpandEdit(objGrd, nRow, nCell, strVal, nPivotIndex){
	var sColIdx     = objGrd.GetCellText("HEAD", nRow, nCell);  //해당 HEAD명 
	switch (sColIdx){
		case "일자" :
			fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
			dsStrtMesurDt3_OnRowPosChanged(dsStrtMesurDt3,null,nRow);
			break;
		default :
			break;
	}
}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	switch(sServiceName) {

         //평가항목
         case "d06000016-service:searchStrtMesurCdPre" :
			dsStrtMesurCd1.UpdateControl = "false";
			var cnt = dsStrtMesurCd1.GetCount();
			for(var i=0; i<cnt; i++) {
				dsStrtMesurCd1.setRowType(i,"Insert");
			}
			break;  
		 //기준표조회
         case "d06000016-service:searchStrtMesurBasPre" :
			dsStrtMesurBas2.UpdateControl = "false";
			var cnt = dsStrtMesurBas2.GetCount();
			for(var i=0; i<cnt; i++) {
				dsStrtMesurBas2.setRowType(i,"Insert");
			}
            break; 
         //일자별체력측정조회     
         case "d06000016-service:searchStrtMesurDt3" :
			  dsStrtMesurDt3.AddColumn("CHK");
			  break; 
         case "d06000016-service:searchStrtMesurDt3_1" :
            break; 
        // 차수별체력측정 조회 콜백
        case "d06000016-service:searchStrtMesurRound5" :
            fcSearchBas();
            break;

		case "d06000016-service:saveStrtMesur" :
			fcSearch();
			break;

		case "d06000016-service:deleteStrtMesurDt3_1" :
			fcSearch();
			break;
		case "d06000016-service:searchPerioNo" :
			fcSearch();
			break;
		default :
			break;
	}

    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리
	if ( obj.GetForm().id == "divBtnRow" ) {

		if(tabMain.TabIndex == 0){ 		//평가항목
			dsStrtMesurCd1.AddRow();
			dsStrtMesurCd1.SetColumn(dsStrtMesurCd1.currow, "RACER_PERIO_NO", cbPerioNo.Value);
			dsStrtMesurCd1.SetColumn(dsStrtMesurCd1.currow, "CHK", "0");
		}
    }        
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( obj.GetForm().id == "divBtnRow" ) {
		if(tabMain.TabIndex == 0){
			if ( !fnc_DeleteCheck(dsStrtMesurCd1, "CHK", "1") ) return;
			     tabMain.tab1.grdStrtMesurCd1.SetFocus(); //Record 삭제 후 Grid로 Focus
		}

		break;
	}
}

//시작시에 검색조건 처리
function fcLoadEnable(){
	if(tabMain.TabIndex == 0){
		cbRound.Enable 			= false;
	}else if(tabMain.TabIndex == 1){
		cbRound.Enable 			= false;		
	}else if(tabMain.TabIndex == 2){
		cbRound.Enable 			= true;	
	}
}	

function tabMain_OnChanged(obj,nOldIndex,nNewindex)
{
	fcLoadEnable();
}

//일자별집게 상세조회
function tabMain_tab4_grdStrtMesurDtTot4_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    var sServiceName 	= "";
    var sInDataSet   	= "";
    var sOutDataSet  	= "";

	for(var i=0; i<dsStrtMesurDtTot4.rowcount; i++) {
		if(i == nRow){
			sRacerPerioNo 	= dsStrtMesurDtTot4.GetColumn(i, "RACER_PERIO_NO");
			sCandNo			= dsStrtMesurDtTot4.GetColumn(i, "CAND_NO");
			break;
		}
	}		

	sServiceName = "d06000016-service:searchStrtMesurDtTot4_1";
	sInDataSet   = "";
	sOutDataSet  = " dsStrtMesurDtTot4_1=dsStrtMesurDtTot4_1 ";	
	fcd_AddParam("racerPerioNo", sRacerPerioNo);
	fcd_AddParam("candNo", sCandNo);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


