function dsStrtMesurRound5_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{

	for(var i=0; i<dsStrtMesurRound5.rowcount; i++) {
        if(!fnc_IsNull(dsStrtMesurRound5.GetColumn(i, "DT"))) {
           tabMain.tab5.CalDt.Value = dsStrtMesurRound5.GetColumn(i, "DT");
            break;
        }
    }
}

function dsItemCdSearch_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	dsItemCdSearch.InsertRow(0);
	dsItemCdSearch.SetColumn(0,"CD_NM","전체");
	//cbItemCd.Index  = 0; 
}

function dsStrtMesurDt3_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID == "DT" ) {
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
			fnc_Message(L_COM_ALT_0016, "N", "날짜");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
			return;
	    }
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
			fnc_Message(L_COM_ALT_0016, "N", "날짜");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
			return;
	    }

        dsStrtMesurDt3_OnRowPosChanged(obj,null,nRow);
     }    
}

function dsStrtMesurDt3_OnRowPosChanged(obj,nOldRow,nRow)
{
	 if ( fnc_IsNull(obj.GetColumn(nRow, "DT")) ) return;

		sServiceName = "d06000016-service:searchStrtMesurDt3_1";
		sInDataSet   = "";
		sOutDataSet  = " dsStrtMesurDt3_1=dsStrtMesurDt3_1 ";	

		fcd_AddParam("racerPerioNo", obj.GetColumn(nRow, "RACER_PERIO_NO"));
		fcd_AddParam("DT"  		   , obj.GetColumn(nRow, "DT"            ));

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

//평가항목 전기수복사
function tabMain_tab1_btnPrevCp_OnClick(obj)
{

	dsStrtMesurCd1.ClearData();
