//기초체력측정 엑셀등록
function cmdExcelUpload_OnClick(obj)
{
	var strFileName;
	var nRow;
	var TR_AMT_IN;
	var TR_AMT_OUT;

	FileDialog.Type = "OPEN";
	FileDialog.Filter = "Excel(.xls)|*.xls|";

	if (!FileDialog.open()){
		return;
	}
	dsOutSheet.ClearData();
	dsStrtMesurRound5.ClearData();
	strFileName = FileDialog.FilePath + "\\" + FileDialog.FileName;
	ext_ExcelImportByIndex(strFileName, 0, dsOutSheet.ID);
	nRow = dsOutSheet.RowCount();

	for (var i = 1; i < nRow; i++)
	{
		dsStrtMesurRound5.AddRow();
		dsOutSheet.CopyToRow(i, dsStrtMesurRound5, i - 1, "CAND_NO=Col02,NM_KOR=Col03,ITEM01_REC=Col04,ITEM02_REC=Col05,ITEM03_REC=Col06,ITEM04_REC=Col07,ITEM05_REC=Col08,ITEM06_REC=Col09,ITEM07_REC=Col10,ITEM08_REC=Col11");

		//신원조회 DATASET에서 성별데이터를 가져온다
		dsStrtMesurRound5.setColumn(i-1,"SEX_GBN",dsStrtMesurDtTot4.GetColumn(dsStrtMesurDtTot4.FindRow("CAND_NO",dsStrtMesurRound5.getColumn(i-1,"CAND_NO")),"SEX_GBN"));
	}

	//총점, 평균, 순위 결정
	for (var i = 0; i < dsStrtMesurRound5.getRowCount(); i++)
	{
		fcSetScr_dsStrtMesur(i, "ITEM01_REC", dsStrtMesurRound5.getColumn(i,"ITEM01_REC"));
		fcSetScr_dsStrtMesur(i, "ITEM02_REC", dsStrtMesurRound5.getColumn(i,"ITEM02_REC"));
		fcSetScr_dsStrtMesur(i, "ITEM03_REC", dsStrtMesurRound5.getColumn(i,"ITEM03_REC"));
		fcSetScr_dsStrtMesur(i, "ITEM04_REC", dsStrtMesurRound5.getColumn(i,"ITEM04_REC"));
		fcSetScr_dsStrtMesur(i, "ITEM05_REC", dsStrtMesurRound5.getColumn(i,"ITEM05_REC"));
		fcSetScr_dsStrtMesur(i, "ITEM06_REC", dsStrtMesurRound5.getColumn(i,"ITEM06_REC"));
		fcSetScr_dsStrtMesur(i, "ITEM07_REC", dsStrtMesurRound5.getColumn(i,"ITEM07_REC"));
		fcSetScr_dsStrtMesur(i, "ITEM08_REC", dsStrtMesurRound5.getColumn(i,"ITEM08_REC"));
		//fcSetScr_dsStrtMesur(i, "ITEM09_REC", dsStrtMesurRound5.getColumn(i,"ITEM09_REC"));
		//fcSetScr_dsStrtMesur(i, "ITEM10_REC", dsStrtMesurRound5.getColumn(i,"ITEM10_REC"));
		//fcSetScr_dsStrtMesur(i, "ITEM11_REC", dsStrtMesurRound5.getColumn(i,"ITEM11_REC"));
	}
}


function cmdSampleUpload_OnClick(obj)
{

	switch(obj.ID){
		case "btnFileDownload1"  : //신체검사
			ExecBrowser(GBL_FILE_UPDNLOAD_URL + "/sampleBodyInsp.xls"); 
			break;
		case "btnFileDownload2"  : //기초체력측정
			ExecBrowser(GBL_FILE_UPDNLOAD_URL + "/sampleStrtMesur.xls"); 
			break;
		default : break;
	}

}
