function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'F'+sInpt+'FTFT';

    //화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	//공통코드 조회
	//fcd_GetComboDs(dsInComCd);


	// 화면 초기화
	fcInit();

	//기수쿼리
	fcPerioNo();


}

function fcInit(obj) {
	dsMain.ClearData();	
}

//기수콤보
function fcPerioNo() {
	var sServiceName = "d06000018-service:searchPerioNo";
    var sInDataSet   = "";
    var sOutDataSet  = " dsPerioNo=dsPerioNo "; 

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

function fcSearchCheck(obj) {
	fcInit(obj);	

	if(length(cbPerioNo.Value) < 1){	
		//기수 필수 저장
		fnc_Message(SNIS_CAN_E001, "N", "기수를 ");
		return;
	}	

	return true;
}

function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";	     

	sServiceName = "d06000018-service:searchEstmMotorMakeTotal";
	sInDataSet   = "";
	sOutDataSet  = " dsMain=dsMain";	
	fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));		
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


function dsMain_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{

	var onedt = "";
	var twodt = "";
	var threedt = "";
	var fourdt = "";

	for(var i=0; i<dsMain.count; i++){
		onedt = dsMain.GetColumn(i,"ROUND1_DT");
		twodt = dsMain.GetColumn(i,"ROUND2_DT");
		threedt = dsMain.GetColumn(i,"ROUND3_DT");
		fourdt = dsMain.GetColumn(i,"ROUND4_DT");
		if ( onedt.length = 8 && twodt.length=8 && threedt.length=8 && fourdt.length=8){ 
			break;
		}
	}
	edtOneDT.text = substr(onedt,0,4)+"-"+substr(onedt,4,2)+"-"+substr(onedt,6,2);
	edtTwoDT.text = substr(twodt,0,4)+"-"+substr(twodt,4,2)+"-"+substr(twodt,6,2);
	edtThreeDT.text = substr(threedt,0,4)+"-"+substr(threedt,4,2)+"-"+substr(threedt,6,2);
	edtFourDT.text = substr(fourdt,0,4)+"-"+substr(fourdt,4,2)+"-"+substr(fourdt,6,2);
}


/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    switch(sServiceName) {

        case "d06000018-service:searchEstmMotorMakeTotal" :

			var onedt = "";
			var twodt = "";
			var threedt = "";
			var fourdt = "";

			for(var i=0; i<dsMain.count; i++){
				onedt = dsMain.GetColumn(i,"ROUND1_DT");
				twodt = dsMain.GetColumn(i,"ROUND2_DT");
				threedt = dsMain.GetColumn(i,"ROUND3_DT");
				fourdt = dsMain.GetColumn(i,"ROUND4_DT");

				if ( onedt.length = 8 && twodt.length=8 && threedt.length=8 && fourdt.length=8){ 
					alert(onedt + twodt + threedt + fourdt);
					break;
				}
			}
			edtOneDT.text = substr(onedt,0,4)+"-"+substr(onedt,4,2)+"-"+substr(onedt,6,2);
			edtTwoDT.text = substr(twodt,0,4)+"-"+substr(twodt,4,2)+"-"+substr(twodt,6,2);
			edtThreeDT.text = substr(threedt,0,4)+"-"+substr(threedt,4,2)+"-"+substr(threedt,6,2);
			edtFourDT.text = substr(fourdt,0,4)+"-"+substr(fourdt,4,2)+"-"+substr(fourdt,6,2);
			break;
          default :
            break;   
    }
     //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}
